import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import LoadingSpinner from '../components/LoadingSpinner';
import { toast } from 'react-toastify';

const products = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics' },
  { id: 2, name: 'Smartphone', price: 45000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics' },
  { id: 3, name: 'T-Shirt', price: 1500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing' },
  { id: 4, name: 'Jeans', price: 2500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing' },
];

const Home = () => {
  const { addToCart } = useCart();
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [filteredProducts, setFilteredProducts] = useState(products);
  const categories = ['All', 'Electronics', 'Clothing'];

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    let result = products;
    if (category !== 'All') {
      result = result.filter(product => product.category === category);
    }
    if (searchTerm) {
      result = result.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    setFilteredProducts(result);
  }, [searchTerm, category]);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">All Products</h2>
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <input
          type="text"
          placeholder="Search products..."
          className="w-full md:w-1/3 p-2 border rounded"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full md:w-1/4 p-2 border rounded"
        >
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-xl text-gray-500">No products found.</p>
        ) : (
          filteredProducts.map(product => (
            <div key={product.id} className="border p-4 rounded shadow hover:shadow-lg transition">
              <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
              <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
              <p className="text-gray-600">{product.category}</p>
              <p className="text-gray-700 mb-2">Rs. {product.price}</p>
              <div className="flex justify-between">
                <Link to={`/product/${product.id}`} className="text-blue-500 hover:underline">View Details</Link>
                <button
                  onClick={() => {
                    addToCart(product);
                    toast.success(`${product.name} added to cart!`);
                  }}
                  className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Home;
