import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { toast } from 'react-toastify';

const Cart = () => {
  const { cartItems, updateQuantity, removeFromCart, clearCart } = useCart();
  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (cartItems.length === 0) {
    return <div className="p-8 text-xl text-center text-gray-500 dark:text-gray-400 bg-white dark:bg-gray-900 min-h-screen">Your cart is empty.</div>;
  }

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6">Your Cart</h2>
      <button
        onClick={clearCart}
        className="mb-6 bg-red-500 dark:bg-red-600 text-white px-4 py-2 rounded hover:bg-red-600 dark:hover:bg-red-700"
      >
        Clear Cart
      </button>
      <div className="space-y-6">
        {cartItems.map(item => (
          <div key={item.id} className="flex items-center justify-between border-b pb-4 border-gray-300 dark:border-gray-600">
            <div className="flex items-center space-x-4">
              <img src={item.imageUrl} alt={item.name} className="w-24 h-24 object-cover rounded" />
              <div>
                <h3 className="text-xl font-semibold">{item.name}</h3>
                <p className="text-gray-600 dark:text-gray-400">Rs. {item.price}</p>
                <div className="flex items-center gap-2 mt-2">
                  <label className="text-lg text-gray-700 dark:text-gray-300">Quantity:</label>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span className="px-4 py-1 border rounded bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-gray-200">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                  >
                    +
                  </button>
                </div>
              </div>
            </div>
            <div>
              <p className="text-lg font-semibold">Rs. {item.price * item.quantity}</p>
              <button
                onClick={() => removeFromCart(item.id)}
                className="mt-2 px-4 py-2 bg-red-500 dark:bg-red-600 text-white rounded hover:bg-red-600 dark:hover:bg-red-700"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-6 text-xl font-bold">
        Total: Rs. {totalPrice}
      </div>
      <Link
        to="/checkout"
        className="mt-6 inline-block bg-blue-500 dark:bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
      >
        Proceed to Checkout
      </Link>
    </div>
  );
};

export default Cart;
