import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';
import SearchSuggestions from '../components/SearchSuggestions';
import QuickViewModal from '../components/QuickViewModal';

const Home = () => {
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [sortBy, setSortBy] = useState('default');
  const [priceFrom, setPriceFrom] = useState('');
  const [priceTo, setPriceTo] = useState('');
  const [priceError, setPriceError] = useState('');
  const [minRating, setMinRating] = useState(0);
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState(JSON.parse(localStorage.getItem('recentlyViewed')) || []);
  const [cartAnimation, setCartAnimation] = useState(null);
  const [compareProducts, setCompareProducts] = useState(JSON.parse(localStorage.getItem('compareProducts')) || []);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observerRef = useRef(null);
  const loadMoreRef = useRef(null);
  const searchTimeoutRef = useRef(null);

  const categories = ['All', ...new Set(products.map(product => product.category))];
  const sortOptions = [
    { value: 'default', label: 'Default' },
    { value: 'price-asc', label: 'Price: Low to High' },
    { value: 'price-desc', label: 'Price: High to Low' },
    { value: 'name-asc', label: 'Name: A to Z' },
    { value: 'name-desc', label: 'Name: Z to A' },
  ];
  const ratingOptions = [
    { value: 0, label: 'All Ratings' },
    { value: 3, label: '3 Stars & Up' },
    { value: 4, label: '4 Stars & Up' },
  ];

  const fetchProducts = async (retries = 3, delay = 1000) => {
    try {
      setIsLoading(true);
      setError(null);
      const fakeStoreResponse = await fetch('https://fakestoreapi.com/products');
      if (!fakeStoreResponse.ok) throw new Error('Failed to fetch from FakeStoreAPI');
      const fakeStoreData = await fakeStoreResponse.json();
      const fakeStoreProducts = fakeStoreData.map(item => ({
        id: `fake-${item.id}`,
        name: item.title,
        price: item.price,
        imageUrl: item.image,
        category: item.category.charAt(0).toUpperCase() + item.category.slice(1),
        rating: item.rating.rate,
        description: item.description,
        stock: Math.floor(Math.random() * 50) + 1, // Random stock
      }));

      const dummyJsonResponse = await fetch('https://dummyjson.com/products?limit=30');
      if (!dummyJsonResponse.ok) throw new Error('Failed to fetch from DummyJSON');
      const dummyJsonData = await dummyJsonResponse.json();
      const dummyJsonProducts = dummyJsonData.products.map(item => ({
        id: `dummy-${item.id}`,
        name: item.title,
        price: item.price,
        imageUrl: item.thumbnail,
        category: item.category.charAt(0).toUpperCase() + item.category.slice(1),
        rating: item.rating,
        description: item.description,
        stock: Math.floor(Math.random() * 50) + 1, // Random stock
      }));

      const combinedProducts = [...fakeStoreProducts, ...dummyJsonProducts];
      setProducts(combinedProducts);
      setIsLoading(false);
    } catch (err) {
      if (retries > 0) {
        setTimeout(() => fetchProducts(retries - 1, delay * 2), delay);
      } else {
        setError('Unable to load products. Please try again.');
        setIsLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const loadProducts = (pageNum) => {
    const start = (pageNum - 1) * 10;
    const end = start + 10;
    let result = products.slice(0, end);

    if (category !== 'All') {
      result = result.filter(product => product.category === category);
    }

    if (searchTerm) {
      result = result.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    const from = parseFloat(priceFrom);
    const to = parseFloat(priceTo);
    if (priceFrom !== '' && priceTo !== '') {
      if (isNaN(from) || isNaN(to)) {
        setPriceError('Please enter valid numbers');
        setFilteredProducts(result);
        return;
      }
      if (from < 0 || to < 0) {
        setPriceError('Prices cannot be negative');
        setFilteredProducts(result);
        return;
      }
      if (from > to) {
        setPriceError('"From" price must be less than or equal to "To" price');
        setFilteredProducts(result);
        return;
      }
      setPriceError('');
      result = result.filter(product => product.price >= from && product.price <= to);
    } else if (priceFrom !== '' || priceTo !== '') {
      setPriceError('Please enter both price fields');
      setFilteredProducts(result);
      return;
    } else {
      setPriceError('');
    }

    if (minRating > 0) {
      result = result.filter(product => product.rating >= minRating);
    }

    if (sortBy !== 'default') {
      result = [...result].sort((a, b) => {
        if (sortBy === 'price-asc') return a.price - b.price;
        if (sortBy === 'price-desc') return b.price - a.price;
        if (sortBy === 'name-asc') return a.name.localeCompare(b.name);
        if (sortBy === 'name-desc') return b.name.localeCompare(a.name);
        return 0;
      });
    }

    setFilteredProducts(result);
    setHasMore(end < products.length);
  };

  useEffect(() => {
    if (products.length > 0) {
      setPage(1);
      loadProducts(1);
    }
  }, [products, searchTerm, category, sortBy, priceFrom, priceTo, minRating]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoading) {
          setPage(prev => prev + 1);
          loadProducts(page + 1);
        }
      },
      { threshold: 0.1 }
    );
    if (loadMoreRef.current) observer.observe(loadMoreRef.current);
    observerRef.current = observer;
    return () => observer.disconnect();
  }, [hasMore, isLoading, page, products]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }
    searchTimeoutRef.current = setTimeout(() => {
      setSearchTerm(value);
    }, 500);
  };

  const handleAddToCart = (product, e) => {
    addToCart({ ...product, quantity: 1 });
    const rect = e.target.getBoundingClientRect();
    setCartAnimation({
      id: product.id,
      x: rect.x,
      y: rect.y,
    });
    setTimeout(() => setCartAnimation(null), 500);
  };

  const handleCompare = (product) => {
    let updatedCompare = compareProducts;
    if (compareProducts.some(p => p.id === product.id)) {
      updatedCompare = compareProducts.filter(p => p.id !== product.id);
    } else if (compareProducts.length < 4) {
      updatedCompare = [...compareProducts, product];
    }
    setCompareProducts(updatedCompare);
    localStorage.setItem('compareProducts', JSON.stringify(updatedCompare));
  };

  const startComparison = () => {
    if (compareProducts.length >= 2) {
      localStorage.setItem('compareProducts', JSON.stringify(compareProducts));
      navigate('/compare');
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return (
      <div className="container mx-auto py-8 text-center text-red-500 dark:text-red-400 min-h-screen">
        <p>{error}</p>
        <button
          onClick={fetchProducts}
          className="mt-4 bg-blue-500 dark:bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 min-h-screen bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200">
      <h2 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">All Products</h2>
      <div className="flex flex-col gap-4 sm:gap-6 mb-4 sm:mb-6">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative w-full sm:w-1/3">
            <input
              type="text"
              placeholder="Search products..."
              className="w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
              onChange={handleSearchChange}
            />
            {searchTerm && (
              <SearchSuggestions
                searchTerm={searchTerm}
                products={products}
                setSearchTerm={setSearchTerm}
              />
            )}
          </div>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full sm:w-1/4 p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
          >
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="w-full sm:w-1/4 p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
          >
            {sortOptions.map(option => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
          </select>
        </div>
        <div className="mb-4 sm:mb-6">
          <h3 className="text-base sm:text-lg font-semibold mb-2">Custom Price Range</h3>
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="w-full sm:w-1/2">
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">From ($)</label>
              <input
                type="number"
                placeholder="e.g., 10"
                value={priceFrom}
                onChange={(e) => {
                  const value = e.target.value;
                  if (value === '' || (Number(value) >= 0 && !isNaN(value))) {
                    setPriceFrom(value);
                  }
                }}
                className="w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
                min="0"
              />
            </div>
            <div className="w-full sm:w-1/2">
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">To ($)</label>
              <input
                type="number"
                placeholder="e.g., 500"
                value={priceTo}
                onChange={(e) => {
                  const value = e.target.value;
                  if (value === '' || (Number(value) >= 0 && !isNaN(value))) {
                    setPriceTo(value);
                  }
                }}
                className="w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
                min="0"
              />
            </div>
          </div>
          {priceError && (
            <p className="text-red-500 dark:text-red-400 text-sm mt-2">{priceError}</p>
          )}
        </div>
        <div className="mb-4 sm:mb-6">
          <h3 className="text-base sm:text-lg font-semibold mb-2">Minimum Rating</h3>
          <select
            value={minRating}
            onChange={(e) => setMinRating(+e.target.value)}
            className="w-full sm:w-1/4 p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
          >
            {ratingOptions.map(option => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
          </select>
        </div>
        <div className="mb-4 sm:mb-6">
          <button
            onClick={startComparison}
            disabled={compareProducts.length < 2}
            className={`w-full sm:w-auto px-4 py-2 rounded text-white text-sm sm:text-base ${compareProducts.length < 2 ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700'}`}
          >
            Compare Selected ({compareProducts.length}/4)
          </button>
        </div>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-base sm:text-xl text-gray-500 dark:text-gray-400 col-span-full text-center">No products found.</p>
        ) : (
          filteredProducts
            .filter(product => product.id && (product.id.startsWith('fake-') || product.id.startsWith('dummy-')))
            .map(product => (
              <div
                key={product.id}
                className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200 relative"
              >
                <input
                  type="checkbox"
                  checked={compareProducts.some(p => p.id === product.id)}
                  onChange={() => handleCompare(product)}
                  className="absolute top-2 right-2"
                  disabled={!compareProducts.some(p => p.id === product.id) && compareProducts.length >= 4}
                />
                <img
                  src={product.imageUrl || '/placeholder-image.jpg'}
                  alt={product.name}
                  className="w-full h-48 sm:h-56 object-contain rounded"
                  loading="lazy"
                  onError={(e) => { e.target.src = '/placeholder-image.jpg'; }}
                />
                <h3 className="text-base sm:text-lg font-semibold mt-2 truncate">{product.name}</h3>
                <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">{product.category}</p>
                <p className="text-gray-700 dark:text-gray-300 text-sm sm:text-base mb-2">$ {product.price.toFixed(2)}</p>
                <p className="text-yellow-500 text-sm sm:text-base mb-2">
                  {'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}
                </p>
                <div className="flex flex-col sm:flex-row justify-between gap-2">
                  <div className="flex flex-col gap-2">
                    <Link
                      to={`/product/${product.id}`}
                      className="text-blue-500 dark:text-blue-400 hover:underline text-sm sm:text-base"
                    >
                      View Details
                    </Link>
                    <button
                      onClick={() => setSelectedProduct(product)}
                      className="text-blue-500 dark:text-blue-400 hover:underline text-sm sm:text-base"
                    >
                      Quick View
                    </button>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={(e) => handleAddToCart(product, e)}
                      className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
                    >
                      Add to Cart
                    </button>
                    <button
                      onClick={() => addToWishlist(product)}
                      className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-600 dark:hover:bg-gray-700 text-sm sm:text-base"
                    >
                      Add to Wishlist
                    </button>
                  </div>
                </div>
                {cartAnimation && cartAnimation.id === product.id && (
                  <img
                    src={product.imageUrl || '/placeholder-image.jpg'}
                    alt={product.name}
                    className="absolute w-12 h-12 object-cover rounded-md animate-fly-to-cart"
                    style={{ margin: 0, padding: '0', top: cartAnimation.y, left: cartAnimation.x }}
                  />
                )}
              </div>
            ))
        )}
      </div>
      {hasMore && (
        <div ref={loadMoreRef} className="flex justify-center py-4">
          <LoadingSpinner />
        </div>
      )}
      {recentlyViewed.length > 0 && (
        <div className="mt-6 sm:mt-8">
          <h3 className="text-xl sm:text-2xl font-bold mb-4">Recently Viewed Products</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-6">
            {recentlyViewed.map(product => (
              <div
                key={product.id}
                className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600"
              >
                <img
                  src={product.imageUrl || '/placeholder-image.jpg'}
                  alt={product.name}
                  className="w-full h-48 sm:h-56 object-contain rounded"
                  loading="lazy"
                  onError={(e) => { e.target.src = '/placeholder-image.jpg'; }}
                />
                <h3 className="text-base sm:text-lg font-semibold mt-2 truncate">{product.name}</h3>
                <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">$ {product.price.toFixed(2)}</p>
                <Link
                  to={`/product/${product.id}`}
                  className="text-blue-500 dark:text-blue-400 hover:underline text-sm sm:text-base"
                >
                  View Details
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}
      {selectedProduct && (
        <QuickViewModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          addToCart={addToCart}
          addToWishlist={addToWishlist}
        />
      )}
    </div>
  );
};

export default Home;
