import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { toast } from 'react-toastify';

const Profile = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')) || {};
  const isLoggedIn = localStorage.getItem('isLoggedIn');
  const [isEditing, setIsEditing] = useState(false);
  const { register, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      fullName: user.fullName || '',
      email: user.email || '',
      phone: user.phone || '',
      gender: user.gender || '',
    },
  });

  if (!isLoggedIn) {
    return (
      <div className="container mx-auto p-6 text-center">
        <h2 className="text-2xl font-bold mb-4">Please Log In</h2>
        <p className="text-lg">You need to be logged in to view your profile.</p>
        <button
          onClick={() => navigate('/login')}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Go to Login
        </button>
      </div>
    );
  }

  const onSubmit = (data) => {
    localStorage.setItem('user', JSON.stringify({
      ...user,
      fullName: data.fullName,
      email: data.email,
      phone: data.phone,
      gender: data.gender,
    }));
    toast.success('Profile updated successfully!');
    setIsEditing(false);
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-6">Your Profile</h2>
      {isEditing ? (
        <form onSubmit={handleSubmit(onSubmit)} className="max-w-md mx-auto space-y-4">
          <div>
            <label className="block mb-1">Full Name</label>
            <input
              name="fullName"
              className={`w-full p-2 border rounded ${errors.fullName ? 'border-red-500' : ''}`}
              {...register('fullName', { required: 'Full Name is required', minLength: { value: 2, message: 'Name must be at least 2 characters' } })}
            />
            {errors.fullName && <p className="text-red-500 text-sm">{errors.fullName.message}</p>}
          </div>
          <div>
            <label className="block mb-1">Email</label>
            <input
              name="email"
              type="email"
              className={`w-full p-2 border rounded ${errors.email ? 'border-red-500' : ''}`}
              {...register('email', { required: 'Email is required', pattern: { value: /^\S+@\S+$/i, message: 'Invalid email address' } })}
            />
            {errors.email && <p className="text-red-500 text-sm">{errors.email.message}</p>}
          </div>
          <div>
            <label className="block mb-1">Phone Number</label>
            <input
              name="phone"
              type="tel"
              className={`w-full p-2 border rounded ${errors.phone ? 'border-red-500' : ''}`}
              placeholder="+92 300 1234567"
              {...register('phone', { 
                required: 'Phone number is required',
                pattern: { value: /^\+?\d{10,13}$/, message: 'Invalid phone number' }
              })}
            />
            {errors.phone && <p className="text-red-500 text-sm">{errors.phone.message}</p>}
          </div>
          <div>
            <label className="block mb-1">Gender</label>
            <select
              name="gender"
              className={`w-full p-2 border rounded ${errors.gender ? 'border-red-500' : ''}`}
              {...register('gender', { required: 'Please select a gender' })}
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            {errors.gender && <p className="text-red-500 text-sm">{errors.gender.message}</p>}
          </div>
          <div className="flex gap-4">
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
            >
              Save Changes
            </button>
            <button
              type="button"
              onClick={() => setIsEditing(false)}
              className="w-full bg-gray-500 text-white py-2 rounded hover:bg-gray-600"
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <div className="max-w-md mx-auto">
          <p className="text-lg"><strong>Name:</strong> {user.fullName}</p>
          <p className="text-lg"><strong>Email:</strong> {user.email}</p>
          <p className="text-lg"><strong>Phone:</strong> {user.phone}</p>
          <p className="text-lg"><strong>Gender:</strong> {user.gender}</p>
          <button
            onClick={() => setIsEditing(true)}
            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};

export default Profile;
