import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';
import SearchSuggestions from '../components/SearchSuggestions';
import QuickViewModal from '../components/QuickViewModal';

const allProducts = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.5, description: 'High-performance laptop.' },
  { id: 2, name: 'Smartphone', price: 45000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.0, description: 'Latest smartphone model.' },
  { id: 3, name: 'T-Shirt', price: 1500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.5, description: 'Comfortable cotton t-shirt.' },
  { id: 4, name: 'Jeans', price: 2500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 4.0, description: 'Stylish denim jeans.' },
  { id: 5, name: 'Headphones', price: 5000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.2, description: 'Wireless headphones.' },
  { id: 6, name: 'Jacket', price: 3500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.8, description: 'Warm winter jacket.' },
  { id: 7, name: 'Smartwatch', price: 15000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.1, description: 'Fitness tracking smartwatch.' },
  { id: 8, name: 'Sneakers', price: 3000, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 4.0, description: 'Comfortable sneakers.' },
  { id: 9, name: 'Tablet', price: 30000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.3, description: 'Portable tablet.' },
  { id: 10, name: 'Hoodie', price: 2000, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.9, description: 'Cozy hoodie.' },
  { id: 11, name: 'Camera', price: 60000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.6, description: 'Professional camera.' },
  { id: 12, name: 'Cap', price: 800, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.7, description: 'Stylish cap.' },
];

const Home = () => {
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [sortBy, setSortBy] = useState('default');
  const [priceFrom, setPriceFrom] = useState('');
  const [priceTo, setPriceTo] = useState('');
  const [priceError, setPriceError] = useState('');
  const [minRating, setMinRating] = useState(0);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState(JSON.parse(localStorage.getItem('recentlyViewed')) || []);
  const [cartAnimation, setCartAnimation] = useState(null);
  const [compareProducts, setCompareProducts] = useState([]);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observerRef = useRef(null);
  const loadMoreRef = useRef(null);
  const categories = ['All', 'Electronics', 'Clothing'];
  const sortOptions = [
    { value: 'default', label: 'Default' },
    { value: 'price-asc', label: 'Price: Low to High' },
    { value: 'price-desc', label: 'Price: High to Low' },
    { value: 'name-asc', label: 'Name: A to Z' },
    { value: 'name-desc', label: 'Name: Z to A' },
  ];
  const ratingOptions = [
    { value: 0, label: 'All Ratings' },
    { value: 3, label: '3 Stars & Up' },
    { value: 4, label: '4 Stars & Up' },
  ];

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
      loadProducts(1);
    }, 2000);
  }, []);

  const loadProducts = (pageNum) => {
    const start = (pageNum - 1) * 10;
    const end = start + 10;
    let result = allProducts.slice(0, end);

    if (category !== 'All') {
      result = result.filter(product => product.category === category);
    }

    if (searchTerm) {
      result = result.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    const from = parseFloat(priceFrom);
    const to = parseFloat(priceTo);
    if (priceFrom && priceTo) {
      if (isNaN(from) || isNaN(to)) {
        setPriceError('Please enter valid numbers');
        setFilteredProducts(result);
        return;
      }
      if (from < 0 || to < 0) {
        setPriceError('Prices cannot be negative');
        setFilteredProducts(result);
        return;
      }
      if (from > to) {
        setPriceError('"From" price must be less than or equal to "To" price');
        setFilteredProducts(result);
        return;
      }
      setPriceError('');
      result = result.filter(product => product.price >= from && product.price <= to);
    } else {
      setPriceError('');
    }

    if (minRating > 0) {
      result = result.filter(product => product.rating >= minRating);
    }

    if (sortBy !== 'default') {
      result = [...result].sort((a, b) => {
        if (sortBy === 'price-asc') return a.price - b.price;
        if (sortBy === 'price-desc') return b.price - a.price;
        if (sortBy === 'name-asc') return a.name.localeCompare(b.name);
        if (sortBy === 'name-desc') return b.name.localeCompare(a.name);
        return 0;
      });
    }

    setFilteredProducts(result);
    setHasMore(end < allProducts.length);
  };

  useEffect(() => {
    setPage(1);
    loadProducts(1);
  }, [searchTerm, category, sortBy, priceFrom, priceTo, minRating]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoading) {
          setPage(prev => prev + 1);
          loadProducts(page + 1);
        }
      },
      { threshold: 0.1 }
    );
    if (loadMoreRef.current) observer.observe(loadMoreRef.current);
    observerRef.current = observer;
    return () => observer.disconnect();
  }, [hasMore, isLoading, page]);

  const handleAddToCart = (product, e) => {
    addToCart(product);
    const rect = e.target.getBoundingClientRect();
    setCartAnimation({
      id: product.id,
      x: rect.x,
      y: rect.y,
    });
    setTimeout(() => setCartAnimation(null), 500);
  };

  const handleCompare = (product) => {
    if (compareProducts.some(p => p.id === product.id)) {
      setCompareProducts(compareProducts.filter(p => p.id !== product.id));
    } else if (compareProducts.length < 4) {
      setCompareProducts([...compareProducts, product]);
    }
  };

  const startComparison = () => {
    if (compareProducts.length >= 2) {
      localStorage.setItem('compareProducts', JSON.stringify(compareProducts));
      navigate('/compare');
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6">All Products</h2>
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <div className="relative w-full md:w-1/3">
          <input
            type="text"
            placeholder="Search products..."
            className="w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {searchTerm && (
            <SearchSuggestions
              searchTerm={searchTerm}
              products={allProducts}
              setSearchTerm={setSearchTerm}
            />
          )}
        </div>
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full md:w-1/4 p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
        >
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="w-full md:w-1/4 p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
        >
          {sortOptions.map(option => (
            <option key={option.value} value={option.value}>{option.label}</option>
          ))}
        </select>
      </div>
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Custom Price Range</h3>
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="w-full sm:w-1/2">
            <label className="block mb-1 text-gray-700 dark:text-gray-300">From (Rs.)</label>
            <input
              type="number"
              placeholder="e.g., 1000"
              value={priceFrom}
              onChange={(e) => setPriceFrom(e.target.value)}
              className="w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
              min="0"
            />
          </div>
          <div className="w-full sm:w-1/2">
            <label className="block mb-1 text-gray-700 dark:text-gray-300">To (Rs.)</label>
            <input
              type="number"
              placeholder="e.g., 50000"
              value={priceTo}
              onChange={(e) => setPriceTo(e.target.value)}
              className="w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
              min="0"
            />
          </div>
        </div>
        {priceError && (
          <p className="text-red-500 dark:text-red-400 text-sm mt-2">{priceError}</p>
        )}
      </div>
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Minimum Rating</h3>
        <select
          value={minRating}
          onChange={(e) => setMinRating(+e.target.value)}
          className="w-full sm:w-1/4 p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
        >
          {ratingOptions.map(option => (
            <option key={option.value} value={option.value}>{option.label}</option>
          ))}
        </select>
      </div>
      <div className="mb-6">
        <button
          onClick={startComparison}
          disabled={compareProducts.length < 2}
          className={`px-4 py-2 rounded text-white ${compareProducts.length < 2 ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700'}`}
        >
          Compare Selected ({compareProducts.length}/4)
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-xl text-gray-500 dark:text-gray-400">No products found.</p>
        ) : (
          filteredProducts.map(product => (
            <div
              key={product.id}
              className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200 relative"
            >
              <input
                type="checkbox"
                checked={compareProducts.some(p => p.id === product.id)}
                onChange={() => handleCompare(product)}
                className="absolute top-2 right-2"
                disabled={!compareProducts.some(p => p.id === product.id) && compareProducts.length >= 4}
              />
              <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
              <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
              <p className="text-gray-600 dark:text-gray-400">{product.category}</p>
              <p className="text-gray-700 dark:text-gray-300 mb-2">Rs. {product.price}</p>
              <p className="text-yellow-500 mb-2">{'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}</p>
              <div className="flex justify-between gap-2">
                <div className="flex flex-col gap-2">
                  <Link to={`/product/${product.id}`} className="text-blue-500 dark:text-blue-400 hover:underline">
                    View Details
                  </Link>
                  <button
                    onClick={() => setSelectedProduct(product)}
                    className="text-blue-500 dark:text-blue-400 hover:underline"
                  >
                    Quick View
                  </button>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={(e) => handleAddToCart(product, e)}
                    className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => addToWishlist(product)}
                    className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
                  >
                    Add to Wishlist
                  </button>
                </div>
              </div>
              {cartAnimation && cartAnimation.id === product.id && (
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  className="absolute w-12 h-12 object-cover rounded animate-fly-to-cart"
                  style={{ top: cartAnimation.y, left: cartAnimation.x }}
                />
              )}
            </div>
          ))
        )}
      </div>
      {hasMore && (
        <div ref={loadMoreRef} className="flex justify-center py-4">
          <LoadingSpinner />
        </div>
      )}
      {recentlyViewed.length > 0 && (
        <div className="mt-8">
          <h3 className="text-2xl font-bold mb-4">Recently Viewed Products</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {recentlyViewed.map(product => (
              <div
                key={product.id}
                className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600"
              >
                <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
                <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
                <p className="text-gray-600 dark:text-gray-400">Rs. {product.price}</p>
                <Link to={`/product/${product.id}`} className="text-blue-500 dark:text-blue-400 hover:underline">
                  View Details
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}
      {selectedProduct && (
        <QuickViewModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          addToCart={addToCart}
          addToWishlist={addToWishlist}
        />
      )}
    </div>
  );
};

export default Home;
