// Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const dummyProducts = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/200' },
  { id: 2, name: 'Phone', price: 45000, imageUrl: 'https://via.placeholder.com/200' },
  { id: 3, name: 'Headphones', price: 8000, imageUrl: 'https://via.placeholder.com/200' },
];

const Home = () => {
  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">Our Products</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {dummyProducts.map(product => (
          <Link
            to={`/product/${product.id}`}
            key={product.id}
            className="border rounded-lg p-4 shadow hover:shadow-lg transition"
          >
            <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded mb-3" />
            <h3 className="text-xl font-semibold">{product.name}</h3>
            <p className="text-gray-600">Rs. {product.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
