import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { toast } from 'react-toastify';

const ProductDetails = () => {
  const { id } = useParams();
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [reviews, setReviews] = useState(
    JSON.parse(localStorage.getItem(`reviews-${id}`)) || []
  );

  const dummyProduct = {
    id: parseInt(id),
    name: `Product ${id}`,
    description: 'This is a sample product description.',
    price: 50000,
    imageUrl: 'https://via.placeholder.com/300',
    category: 'Electronics',
    rating: 4.0,
  };

  useEffect(() => {
    const recentlyViewed = JSON.parse(localStorage.getItem('recentlyViewed')) || [];
    const updatedViewed = [
      dummyProduct,
      ...recentlyViewed.filter(p => p.id !== dummyProduct.id)
    ].slice(0, 3); // Keep last 3 viewed
    localStorage.setItem('recentlyViewed', JSON.stringify(updatedViewed));
  }, [id]);

  const handleRating = (value) => {
    setRating(value);
  };

  const handleReviewSubmit = (e) => {
    e.preventDefault();
    if (!rating || !review.trim()) {
      toast.error('Please provide a rating and review.');
      return;
    }
    const newReview = {
      id: Date.now(),
      rating,
      text: review,
      user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')).fullName : 'Anonymous',
      date: new Date().toLocaleDateString(),
    };
    const updatedReviews = [...reviews, newReview];
    setReviews(updatedReviews);
    localStorage.setItem(`reviews-${id}`, JSON.stringify(updatedReviews));
    toast.success('Review submitted successfully!');
    setRating(0);
    setReview('');
  };

  const handleQuantityChange = (change) => {
    setQuantity(prev => Math.max(1, prev + change));
  };

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6">{dummyProduct.name}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <img src={dummyProduct.imageUrl} alt={dummyProduct.name} className="w-full h-96 object-cover rounded-lg" />
        <div>
          <p className="text-gray-600 dark:text-gray-400 mb-4">{dummyProduct.description}</p>
          <p className="text-gray-600 dark:text-gray-400 mb-4">Category: {dummyProduct.category}</p>
          <p className="text-2xl font-bold mb-4">Rs. {dummyProduct.price}</p>
          <p className="text-yellow-500 mb-4">{'★'.repeat(Math.floor(dummyProduct.rating))}{'☆'.repeat(5 - Math.floor(dummyProduct.rating))}</p>
          <div className="flex items-center gap-4 mb-4">
            <label className="text-lg text-gray-700 dark:text-gray-300">Quantity:</label>
            <div className="flex items-center gap-2">
              <button
                onClick={() => handleQuantityChange(-1)}
                className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                disabled={quantity <= 1}
              >
                -
              </button>
              <span className="px-4 py-1 border rounded bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-gray-200">{quantity}</span>
              <button
                onClick={() => handleQuantityChange(1)}
                className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
              >
                +
              </button>
            </div>
          </div>
          <div className="flex gap-4">
            <button
              onClick={() => addToCart(dummyProduct, quantity)}
              className="bg-blue-500 dark:bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
            >
              Add to Cart
            </button>
            <button
              onClick={() => addToWishlist(dummyProduct)}
              className="bg-gray-500 dark:bg-gray-600 text-white px-6 py-2 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
            >
              Add to Wishlist
            </button>
          </div>
        </div>
      </div>
      <div className="mt-8">
        <h3 className="text-2xl font-semibold mb-4">Customer Reviews</h3>
        {reviews.length === 0 ? (
          <p className="text-gray-500 dark:text-gray-400">No reviews yet. Be the first to review!</p>
        ) : (
          <div className="space-y-4">
            {reviews.map(review => (
              <div key={review.id} className="border-b pb-4 border-gray-300 dark:border-gray-600">
                <div className="flex items-center gap-2">
                  <span className="text-yellow-500">{'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}</span>
                  <span className="text-gray-600 dark:text-gray-400">by {review.user} on {review.date}</span>
                </div>
                <p className="text-gray-700 dark:text-gray-300">{review.text}</p>
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="mt-8">
        <h3 className="text-2xl font-semibold mb-4">Write a Review</h3>
        <form onSubmit={handleReviewSubmit} className="space-y-4 max-w-md">
          <div>
            <label className="block mb-1 text-gray-700 dark:text-gray-300">Rating</label>
            <div className="flex gap-1">
              {[1, 2, 3, 4, 5].map(star => (
                <span
                  key={star}
                  className={`cursor-pointer text-2xl ${star <= rating ? 'text-yellow-500' : 'text-gray-300 dark:text-gray-500'}`}
                  onClick={() => handleRating(star)}
                >
                  ★
                </span>
              ))}
            </div>
          </div>
          <div>
            <label className="block mb-1 text-gray-700 dark:text-gray-300">Your Review</label>
            <textarea
              value={review}
              onChange={(e) => setReview(e.target.value)}
              className="w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
              rows="4"
              placeholder="Write your review here..."
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
          >
            Submit Review
          </button>
        </form>
      </div>
    </div>
  );
};

export default ProductDetails;
