// src/components/ProductList.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import QuickViewModal from './QuickViewModal';
import LoadingSpinner from './LoadingSpinner';

const ProductList = () => {
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartAnimation, setCartAnimation] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 20;

  // Generate mock products to supplement API data
  const generateMockProducts = (count, startId) => {
    const categories = ['Electronics', 'Clothing', 'Accessories', 'Home', 'Beauty'];
    const mockProducts = [];
    for (let i = 0; i < count; i++) {
      const category = categories[Math.floor(Math.random() * categories.length)];
      mockProducts.push({
        id: `mock-${startId + i}`,
        name: `Product ${startId + i} - ${category}`,
        price: parseFloat((Math.random() * 200 + 10).toFixed(2)),
        imageUrl: `https://picsum.photos/200/300?random=${startId + i}`,
        category,
        rating: parseFloat((Math.random() * 2 + 3).toFixed(1)), // 3.0 to 5.0
        description: `Description for Product ${startId + i} in ${category} category.`,
        stock: Math.floor(Math.random() * 50) + 1,
      });
    }
    return mockProducts;
  };

  const fetchProducts = async (retries = 3, delay = 1000) => {
    try {
      setIsLoading(true);
      setError(null);

      // Fetch all products from FakeStoreAPI (no limit parameter)
      const fakeStoreResponse = await fetch('https://fakestoreapi.com/products');
      if (!fakeStoreResponse.ok) throw new Error('Failed to fetch from FakeStoreAPI');
      const fakeStoreData = await fakeStoreResponse.json();
      const fakeStoreProducts = fakeStoreData.map(item => ({
        id: `fake-${item.id}`,
        name: item.title,
        price: item.price,
        imageUrl: item.image,
        category: item.category.charAt(0).toUpperCase() + item.category.slice(1),
        rating: item.rating.rate,
        description: item.description,
        stock: Math.floor(Math.random() * 50) + 1,
      }));

      // Fetch 100 products from DummyJSON
      const dummyJsonResponse = await fetch('https://dummyjson.com/products?limit=100');
      if (!dummyJsonResponse.ok) throw new Error('Failed to fetch from DummyJSON');
      const dummyJsonData = await dummyJsonResponse.json();
      const dummyJsonProducts = dummyJsonData.products.map(item => ({
        id: `dummy-${item.id}`,
        name: item.title,
        price: item.price,
        imageUrl: item.thumbnail,
        category: item.category.charAt(0).toUpperCase() + item.category.slice(1),
        rating: item.rating,
        description: item.description,
        stock: Math.floor(Math.random() * 50) + 1,
      }));

      // Combine API products (~120) and add ~130 mock products
      const apiProducts = [...fakeStoreProducts, ...dummyJsonProducts];
      const mockProducts = generateMockProducts(130, apiProducts.length + 1);
      const combinedProducts = [...apiProducts, ...mockProducts];

      setProducts(combinedProducts);
      setIsLoading(false);
    } catch (err) {
      if (retries > 0) {
        setTimeout(() => fetchProducts(retries - 1, delay * 2), delay);
      } else {
        setError('Unable to load products. Please try again.');
        setIsLoading(false);
      }
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddToCart = (product, e) => {
    addToCart({ ...product, quantity: 1 });
    const rect = e.target.getBoundingClientRect();
    setCartAnimation({
      id: product.id,
      x: rect.x,
      y: rect.y,
    });
    setTimeout(() => setCartAnimation(null), 600);
  };

  // Pagination logic
  const totalPages = Math.ceil(products.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = products.slice(startIndex, startIndex + productsPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return (
      <div className="text-center text-red-500 dark:text-red-400">
        <p className="text-xl">{error}</p>
        <button
          onClick={fetchProducts}
          className="mt-4 bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-8 py-3 rounded-full hover:from-indigo-600 hover:to-purple-700 transform hover:scale-105 transition-all duration-300 shadow-lg"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {currentProducts.map((product, index) => (
          <div
            key={product.id}
            className="product-card bg-white/80 dark:bg-gray-800/80 backdrop-blur-md rounded-xl shadow-xl overflow-hidden transform transition-all duration-500 animate-fade-in"
            style={{ animationDelay: `${index * 100}ms` }}
          >
            <img
              src={product.imageUrl || '/placeholder-image.jpg'}
              alt={product.name}
              className="w-full h-56 object-contain p-4"
              loading="lazy"
              onError={(e) => (e.target.src = '/placeholder-image.jpg')}
            />
            <div className="p-4">
              <h3 className="text-lg font-semibold truncate">{product.name}</h3>
              <p className="text-gray-600 dark:text-gray-400 text-sm">{product.category}</p>
              <p className="text-gray-900 dark:text-gray-100 font-bold text-lg mt-1">
                ${product.price.toFixed(2)}
              </p>
              <p className="text-yellow-500 text-sm mt-1">
                {'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}
              </p>
              <div className="flex flex-col gap-2 mt-4">
                <Link
                  to={`/product/${product.id}`}
                  className="text-indigo-500 dark:text-indigo-400 hover:underline text-sm"
                >
                  View Details
                </Link>
                <button
                  onClick={() => setSelectedProduct(product)}
                  className="text-indigo-500 dark:text-indigo-400 hover:underline text-sm"
                >
                  Quick View
                </button>
              </div>
              <div className="flex gap-2 mt-4">
                <button
                  onClick={(e) => handleAddToCart(product, e)}
                  className="flex-1 bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-4 py-2 rounded-full hover:from-indigo-600 hover:to-purple-700 transform hover:scale-105 transition-all duration-300 shadow-md"
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => addToWishlist(product)}
                  className="flex-1 bg-gray-500 dark:bg-gray-600 text-white px-4 py-2 rounded-full hover:bg-gray-600 dark:hover:bg-gray-700 transform hover:scale-105 transition-all duration-300 shadow-md"
                >
                  Wishlist
                </button>
              </div>
            </div>
            {cartAnimation && cartAnimation.id === product.id && (
              <img
                src={product.imageUrl || '/placeholder-image.jpg'}
                alt={product.name}
                className="absolute w-12 h-12 object-cover rounded-full animate-fly-to-cart shadow-lg"
                style={{ top: cartAnimation.y, left: cartAnimation.x }}
              />
            )}
          </div>
        ))}
      </div>
      {/* Pagination Controls */}
      {totalPages > 1 && (
        <div className="flex justify-center items-center gap-2 mt-8">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
            className="px-4 py-2 bg-indigo-500 text-white rounded-full disabled:bg-gray-300 dark:disabled:bg-gray-600 hover:bg-indigo-600 transition"
          >
            Previous
          </button>
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index + 1}
              onClick={() => handlePageChange(index + 1)}
              className={`px-4 py-2 rounded-full ${
                currentPage === index + 1
                  ? 'bg-indigo-600 text-white'
                  : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 hover:bg-indigo-500 hover:text-white'
              } transition`}
            >
              {index + 1}
            </button>
          ))}
          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-4 py-2 bg-indigo-500 text-white rounded-full disabled:bg-gray-300 dark:disabled:bg-gray-600 hover:bg-indigo-600 transition"
          >
            Next
          </button>
        </div>
      )}
      {selectedProduct && (
        <QuickViewModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          addToCart={addToCart}
          addToWishlist={addToWishlist}
        />
      )}
    </div>
  );
};

export default ProductList;
