import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import Button from '../components/Button';
import Input from '../components/Input';
import { Helmet } from 'react-helmet-async';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { login } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      const success = await login({ email: data.email, password: data.password });
      if (!success) {
        toast.error('Login failed. Please check your credentials.');
      }
    } catch (error) {
      toast.error('An error occurred during login. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    toast.info('Google SSO not implemented in demo. Contact admin for full implementation.');
  };

  const handleForgotPassword = () => {
    toast.info('Forgot Password functionality not implemented in demo. Contact admin for full implementation.');
  };

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8 bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 text-gray-900 dark:text-gray-100 min-h-screen flex items-center justify-center">
      <Helmet>
        <title>Login - ShopEase</title>
        <meta name="description" content="Login to your ShopEase account to start shopping." />
      </Helmet>
      <div className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md rounded-xl shadow-xl p-8 w-full max-w-md animate-fade-in">
        <h2 className="text-3xl font-bold mb-6 text-center">Login to ShopEase</h2>
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div>
            <Input
              name="email"
              type="email"
              placeholder="Enter your email"
              {...register('email', {
                required: 'Email is required',
                pattern: { 
                  value: /^\S+@\S+\.\S+$/, 
                  message: 'Please enter a valid email address' 
                },
              })}
              error={errors.email?.message}
              className="w-full"
            />
          </div>
          <div>
            <Input
              name="password"
              type="password"
              placeholder="Enter your password"
              {...register('password', {
                required: 'Password is required',
                minLength: { 
                  value: 6, 
                  message: 'Password must be at least 6 characters' 
                },
              })}
              error={errors.password?.message}
              className="w-full"
            />
          </div>
          <div className="text-right">
            <button
              type="button"
              onClick={handleForgotPassword}
              className="text-sm text-indigo-500 dark:text-indigo-400 hover:underline"
            >
              Forgot Password?
            </button>
          </div>
          <Button 
            type="submit" 
            variant="primary" 
            size="lg" 
            disabled={isLoading}
            className="w-full"
          >
            {isLoading ? 'Logging in...' : 'Login'}
          </Button>
        </form>
        <div className="mt-6 text-center">
          <Button 
            variant="danger" 
            size="lg" 
            onClick={handleGoogleLogin}
            className="w-full"
          >
            Login with Google
          </Button>
        </div>
        <p className="mt-6 text-center text-gray-600 dark:text-gray-400">
          Don't have an account?{' '}
          <Link to="/register" className="text-indigo-500 dark:text-indigo-400 hover:underline">
            Register
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
