import React from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cartItems } = useCart();

  const totalPrice = cartItems.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );

  const handleCheckout = () => {
    alert('Checkout successful! Payment gateway integration baad mein karenge.');
    // Yahan payment aur order submission logic aayegi aage chal ke
  };

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">Checkout</h2>

      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div className="mb-6">
            {cartItems.map(item => (
              <div key={item.id} className="flex justify-between items-center mb-4 border-b pb-4">
                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p>
                    Rs. {item.price} Ã— {item.quantity}
                  </p>
                </div>
                <p className="font-bold">Rs. {item.price * item.quantity}</p>
              </div>
            ))}
          </div>

          <div className="text-right mb-6">
            <p className="text-2xl font-bold">Total: Rs. {totalPrice}</p>
          </div>

          <button
            onClick={handleCheckout}
            className="bg-green-600 text-white px-6 py-3 rounded hover:bg-green-700"
          >
            Pay Now
          </button>
        </>
      )}
    </div>
  );
};

export default Checkout;
