// pages/Cart.jsx
import React from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cartItems, updateQuantity, removeFromCart } = useCart();

  const getTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  if (cartItems.length === 0) {
    return <div className="p-8 text-xl">Your cart is empty.</div>;
  }

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">Shopping Cart</h2>
      <div className="space-y-6">
        {cartItems.map(item => (
          <div key={item.id} className="flex items-center justify-between border-b pb-4">
            <div className="flex items-center space-x-4">
              <img src={item.imageUrl} alt={item.name} className="w-24 h-24 object-cover rounded" />
              <div>
                <h3 className="text-xl font-semibold">{item.name}</h3>
                <p className="text-gray-600">Rs. {item.price}</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <button
                onClick={() => updateQuantity(item.id, item.quantity - 1)}
                disabled={item.quantity === 1}
                className="px-2 py-1 bg-gray-200 hover:bg-gray-300 rounded"
              >âˆ’</button>
              <span>{item.quantity}</span>
              <button
                onClick={() => updateQuantity(item.id, item.quantity + 1)}
                className="px-2 py-1 bg-gray-200 hover:bg-gray-300 rounded"
              >+</button>
              <button
                onClick={() => removeFromCart(item.id)}
                className="ml-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >Remove</button>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-8 text-right">
        <h3 className="text-2xl font-bold">Total: Rs. {getTotal()}</h3>
        <button className="mt-4 px-6 py-2 bg-green-500 text-white rounded hover:bg-green-600">
          Proceed to Checkout
        </button>
      </div>
    </div>
  );
};

export default Cart;
