import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';

const allProducts = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.5 },
  { id: 2, name: 'Smartphone', price: 45000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.0 },
  { id: 3, name: 'T-Shirt', price: 1500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.5 },
  { id: 4, name: 'Jeans', price: 2500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 4.0 },
  { id: 5, name: 'Headphones', price: 5000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.2 },
  { id: 6, name: 'Jacket', price: 3500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.8 },
  { id: 7, name: 'Smartwatch', price: 15000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.1 },
  { id: 8, name: 'Sneakers', price: 3000, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 4.0 },
  { id: 9, name: 'Tablet', price: 30000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.3 },
  { id: 10, name: 'Hoodie', price: 2000, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.9 },
  { id: 11, name: 'Camera', price: 60000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics', rating: 4.6 },
  { id: 12, name: 'Cap', price: 800, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing', rating: 3.7 },
];

const Category = () => {
  const { name } = useParams();
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [compareProducts, setCompareProducts] = useState(JSON.parse(localStorage.getItem('compareProducts')) || []);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observerRef = useRef(null);
  const loadMoreRef = useRef(null);

  const loadProducts = (pageNum) => {
    const start = (pageNum - 1) * 10;
    const end = start + 10;
    let result = allProducts
      .filter(product => product.category.toLowerCase() === name.toLowerCase())
      .slice(0, end);

    setFilteredProducts(result);
    setHasMore(end < allProducts.filter(product => product.category.toLowerCase() === name.toLowerCase()).length);
  };

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
      loadProducts(1);
    }, 2000);
  }, [name]);

  useEffect(() => {
    setPage(1);
    loadProducts(1);
  }, [name]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoading) {
          setPage(prev => prev + 1);
          loadProducts(page + 1);
        }
      },
      { threshold: 0.1 }
    );
    if (loadMoreRef.current) observer.observe(loadMoreRef.current);
    observerRef.current = observer;
    return () => observer.disconnect();
  }, [hasMore, isLoading, page]);

  const handleCompare = (product) => {
    if (compareProducts.some(p => p.id === product.id)) {
      setCompareProducts(compareProducts.filter(p => p.id !== product.id));
    } else if (compareProducts.length < 4) {
      setCompareProducts([...compareProducts, product]);
    }
    localStorage.setItem('compareProducts', JSON.stringify(compareProducts));
  };

  const startComparison = () => {
    if (compareProducts.length >= 2) {
      localStorage.setItem('compareProducts', JSON.stringify(compareProducts));
      navigate('/compare');
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 capitalize">{name} Products</h2>
      <div className="mb-6">
        <button
          onClick={startComparison}
          disabled={compareProducts.length < 2}
          className={`px-4 py-2 rounded text-white ${compareProducts.length < 2 ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700'}`}
        >
          Compare Selected ({compareProducts.length}/4)
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-xl text-gray-500 dark:text-gray-400">No products found in this category.</p>
        ) : (
          filteredProducts.map(product => (
            <div
              key={product.id}
              className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200 relative"
            >
              <input
                type="checkbox"
                checked={compareProducts.some(p => p.id === product.id)}
                onChange={() => handleCompare(product)}
                className="absolute top-2 right-2"
                disabled={!compareProducts.some(p => p.id === product.id) && compareProducts.length >= 4}
              />
              <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
              <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
              <p className="text-gray-600 dark:text-gray-400">{product.category}</p>
              <p className="text-gray-700 dark:text-gray-300 mb-2">Rs. {product.price}</p>
              <p className="text-yellow-500 mb-2">{'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}</p>
              <div className="flex justify-between gap-2">
                <Link to={`/product/${product.id}`} className="text-blue-500 dark:text-blue-400 hover:underline">
                  View Details
                </Link>
                <div className="flex gap-2">
                  <button
                    onClick={() => addToCart(product)}
                    className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => addToWishlist(product)}
                    className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
                  >
                    Add to Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
      {hasMore && (
        <div ref={loadMoreRef} className="flex justify-center py-4">
          <LoadingSpinner />
        </div>
      )}
    </div>
  );
};

export default Category;
