import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();

  const onSubmit = (data) => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.email === data.email && user.password === data.password) {
      localStorage.setItem('isLoggedIn', true);
      navigate('/');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="flex justify-center mt-10">
      <form onSubmit={handleSubmit(onSubmit)} className="bg-white p-6 rounded shadow-md w-80">
        <h2 className="text-xl font-bold mb-4">Login</h2>
        <div className="mb-3">
          <input
            type="email"
            placeholder="Email"
            className={`w-full p-2 border rounded ${errors.email ? 'border-red-500' : ''}`}
            {...register('email', { required: 'Email is required', pattern: { value: /^\S+@\S+$/i, message: 'Invalid email' } })}
          />
          {errors.email && <p className="text-red-500 text-sm">{errors.email.message}</p>}
        </div>
        <div className="mb-3">
          <input
            type="password"
            placeholder="Password"
            className={`w-full p-2 border rounded ${errors.password ? 'border-red-500' : ''}`}
            {...register('password', { required: 'Password is required', minLength: { value: 6, message: 'Password must be at least 6 characters' } })}
          />
          {errors.password && <p className="text-red-500 text-sm">{errors.password.message}</p>}
        </div>
        <button type="submit" className="bg-blue-500 text-white w-full py-2 rounded hover:bg-blue-600">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
