import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';

const Compare = () => {
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const [compareProducts, setCompareProducts] = useState(JSON.parse(localStorage.getItem('compareProducts')) || []);

  const removeProduct = (id) => {
    const updated = compareProducts.filter(p => p.id !== id);
    setCompareProducts(updated);
    localStorage.setItem('compareProducts', JSON.stringify(updated));
  };

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">Compare Products</h2>
      {compareProducts.length < 2 ? (
        <p className="text-base sm:text-xl text-gray-500 dark:text-gray-400">
          Please select at least 2 products to compare.{' '}
          <Link to="/" className="text-blue-500 dark:text-blue-400 hover:underline">Go back to Home</Link>
        </p>
      ) : (
        <div className="overflow-x-auto">
          <table className="w-full border-collapse text-sm sm:text-base">
            <thead>
              <tr className="bg-gray-100 dark:bg-gray-800">
                <th className="p-2 sm:p-4 text-left border border-gray-300 dark:border-gray-600"></th>
                {compareProducts.map(product => (
                  <th key={product.id} className="p-2 sm:p-4 text-left border border-gray-300 dark:border-gray-600">
                    <button
                      onClick={() => removeProduct(product.id)}
                      className="text-red-500 dark:text-red-400 hover:underline mb-2 text-xs sm:text-sm"
                    >
                      Remove
                    </button>
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="w-20 h-20 sm:w-24 sm:h-24 object-contain rounded mx-auto"
                    />
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Name</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600 truncate">{product.name}</td>
                ))}
              </tr>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Price</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600">$ {product.price.toFixed(2)}</td>
                ))}
              </tr>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Category</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600">{product.category}</td>
                ))}
              </tr>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Rating</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600">
                    {'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}
                  </td>
                ))}
              </tr>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Description</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600 truncate">{product.description}</td>
                ))}
              </tr>
              <tr>
                <td className="p-2 sm:p-4 font-semibold border border-gray-300 dark:border-gray-600">Actions</td>
                {compareProducts.map(product => (
                  <td key={product.id} className="p-2 sm:p-4 border border-gray-300 dark:border-gray-600">
                    <div className="flex flex-col sm:flex-row gap-2">
                      <button
                        onClick={() => addToCart(product)}
                        className="bg-blue-500 dark:bg-blue-600 text-white px-2 sm:px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-xs sm:text-base"
                      >
                        Add to Cart
                      </button>
                      <button
                        onClick={() => addToWishlist(product)}
                        className="bg-gray-500 dark:bg-gray-600 text-white px-2 sm:px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700 text-xs sm:text-base"
                      >
                        Add to Wishlist
                      </button>
                    </div>
                  </td>
                ))}
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Compare;
