import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const { cartItems } = useCart();
  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const navigate = useNavigate();

  // localStorage se check karo kya user logged in hai
  const isLoggedIn = localStorage.getItem('isLoggedIn');

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    navigate('/login');  // Logout ke baad login page par bhejo
  };

  return (
    <nav className="bg-gray-800 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-2xl font-bold">MyShop</Link>
        <div className="space-x-4">

          <Link to="/" className="text-white hover:text-gray-300">Home</Link>
          <Link to="/cart" className="text-white hover:text-gray-300">
            Cart ({totalItems})
          </Link>
          <Link to="/checkout" className="text-white hover:text-gray-300">Checkout</Link>

          {!isLoggedIn ? (
            <>
              <Link to="/login" className="text-white hover:text-gray-300">Login</Link>
              <Link to="/signup" className="text-white hover:text-gray-300">Signup</Link>
            </>
          ) : (
            <button 
              onClick={handleLogout} 
              className="text-white hover:text-gray-300 bg-red-600 px-3 py-1 rounded"
            >
              Logout
            </button>
          )}

        </div>
      </div>
    </nav>
  );
};

export default Navbar;
