import React from 'react';

const Cart = () => {
  const dummyCartItems = [
    { id: 1, name: 'Laptop', price: 85000, quantity: 1 },
    { id: 2, name: 'Smartphone', price: 45000, quantity: 2 },
  ];

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">Your Cart</h2>
      {dummyCartItems.length === 0 ? (
        <p className="text-gray-600">Your cart is empty.</p>
      ) : (
        <div className="space-y-4">
          {dummyCartItems.map(item => (
            <div key={item.id} className="flex justify-between items-center border-b pb-4">
              <div>
                <h3 className="text-lg font-semibold">{item.name}</h3>
                <p className="text-gray-600">Rs. {item.price} x {item.quantity}</p>
              </div>
              <p className="text-xl font-bold">Rs. {item.price * item.quantity}</p>
            </div>
          ))}
          <div className="text-right">
            <p className="text-2xl font-bold">Total: Rs. {dummyCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0)}</p>
            <button className="mt-4 bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600">Proceed to Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
