import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { toast } from 'react-toastify';

const Cart = () => {
  const { cartItems, updateQuantity, removeFromCart, clearCart } = useCart();
  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (cartItems.length === 0) {
    return (
      <div className="p-4 sm:p-8 text-base sm:text-xl text-center text-gray-500 dark:text-gray-400 bg-white dark:bg-gray-900 min-h-screen">
        Your cart is empty.
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">Your Cart</h2>
      <button
        onClick={clearCart}
        className="mb-4 sm:mb-6 bg-red-500 dark:bg-red-600 text-white px-3 sm:px-4 py-1 sm:py-2 rounded hover:bg-red-600 dark:hover:bg-red-700 text-sm sm:text-base"
      >
        Clear Cart
      </button>
      <div className="space-y-4 sm:space-y-6">
        {cartItems.map(item => (
          <div
            key={item.id}
            className="flex flex-col sm:flex-row items-start sm:items-center justify-between border-b pb-4 border-gray-300 dark:border-gray-600"
          >
            <div className="flex items-center space-x-4 w-full sm:w-auto">
              <img
                src={item.imageUrl}
                alt={item.name}
                className="w-20 h-20 sm:w-24 sm:h-24 object-contain rounded"
              />
              <div>
                <h3 className="text-base sm:text-xl font-semibold truncate">{item.name}</h3>
                <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">$ {item.price.toFixed(2)}</p>
                <div className="flex items-center gap-2 mt-2">
                  <label className="text-sm sm:text-lg text-gray-700 dark:text-gray-300">Quantity:</label>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-2 sm:px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span className="px-3 sm:px-4 py-1 border rounded bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-gray-200 text-sm sm:text-base">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-2 sm:px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                  >
                    +
                  </button>
                </div>
              </div>
            </div>
            <div className="mt-4 sm:mt-0">
              <p className="text-base sm:text-lg font-semibold">$ {(item.price * item.quantity).toFixed(2)}</p>
              <button
                onClick={() => removeFromCart(item.id)}
                className="mt-2 px-3 sm:px-4 py-1 sm:py-2 bg-red-500 dark:bg-red-600 text-white rounded hover:bg-red-600 dark:hover:bg-red-700 text-sm sm:text-base"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-4 sm:mt-6 text-base sm:text-xl font-bold">
        Total: $ {totalPrice.toFixed(2)}
      </div>
      <Link
        to="/checkout"
        className="mt-4 sm:mt-6 inline-block bg-blue-500 dark:bg-blue-600 text-white px-4 sm:px-6 py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
      >
        Proceed to Checkout
      </Link>
    </div>
  );
};

export default Cart;
