import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';

const Category = () => {
  const { name } = useParams();
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const navigate = useNavigate();

  const [isLoading, setIsLoading] = useState(true);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [compareProducts, setCompareProducts] = useState(JSON.parse(localStorage.getItem('compareProducts')) || []);
  const loadMoreRef = useRef(null);

  // ✅ Fetch products from DummyJSON API
  useEffect(() => {
    setIsLoading(true);
    fetch(`https://dummyjson.com/products/category/${name.toLowerCase()}`)
      .then(res => res.json())
      .then(data => {
        setFilteredProducts(data.products);
        setIsLoading(false);
      })
      .catch(error => {
        console.error("Error fetching products:", error);
        setIsLoading(false);
      });
  }, [name]);

  // ✅ Handle compare
  const handleCompare = (product) => {
    let updatedCompare = [];

    if (compareProducts.some(p => p.id === product.id)) {
      updatedCompare = compareProducts.filter(p => p.id !== product.id);
    } else if (compareProducts.length < 4) {
      updatedCompare = [...compareProducts, product];
    } else {
      updatedCompare = [...compareProducts];
    }

    setCompareProducts(updatedCompare);
    localStorage.setItem('compareProducts', JSON.stringify(updatedCompare));
  };

  // ✅ Start comparison page
  const startComparison = () => {
    if (compareProducts.length >= 2) {
      localStorage.setItem('compareProducts', JSON.stringify(compareProducts));
      navigate('/compare');
    }
  };

  if (isLoading) return <LoadingSpinner />;

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 capitalize">{name} Products</h2>

      <div className="mb-6">
        <button
          onClick={startComparison}
          disabled={compareProducts.length < 2}
          className={`px-4 py-2 rounded text-white ${compareProducts.length < 2 ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700'}`}
        >
          Compare Selected ({compareProducts.length}/4)
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-xl text-gray-500 dark:text-gray-400">No products found in this category.</p>
        ) : (
          filteredProducts.map(product => (
            <div
              key={product.id}
              className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200 relative"
            >
              <input
                type="checkbox"
                checked={compareProducts.some(p => p.id === product.id)}
                onChange={() => handleCompare(product)}
                className="absolute top-2 right-2"
                disabled={!compareProducts.some(p => p.id === product.id) && compareProducts.length >= 4}
              />
              <img
                src={product.thumbnail || product.images?.[0]}
                alt={product.title}
                className="w-full h-48 object-cover rounded mb-2"
              />
              <h3 className="text-xl font-semibold">{product.title}</h3>
              <p className="text-gray-600 dark:text-gray-400 capitalize">{product.category}</p>
              <p className="text-gray-700 dark:text-gray-300 mb-2">Rs. {product.price}</p>
              <p className="text-yellow-500 mb-2">{'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}</p>
              <div className="flex justify-between gap-2">
                <Link to={`/product/${product.id}`} className="text-blue-500 dark:text-blue-400 hover:underline">
                  View Details
                </Link>
                <div className="flex gap-2">
                  <button
                    onClick={() => addToCart(product)}
                    className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => addToWishlist(product)}
                    className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
                  >
                    Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      <div ref={loadMoreRef}></div>
    </div>
  );
};

export default Category;
