import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const Signup = () => {
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const navigate = useNavigate();

  const onSubmit = (data) => {
    if (data.password !== data.confirmPassword) {
      toast.error("Password and Confirm Password don't match");
      return;
    }
    if (!data.acceptedTerms) {
      toast.error("Please accept the terms and conditions");
      return;
    }

    // Save user data to localStorage
    localStorage.setItem('isLoggedIn', false); // Initially not logged in
    localStorage.setItem('user', JSON.stringify({
      fullName: data.fullName,
      email: data.email,
      password: data.password,
      phone: data.phone,
      gender: data.gender,
    }));
    toast.success("Signup successful! Please login to continue.");
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className="max-w-md mx-auto p-6 border rounded shadow mt-8">
      <h2 className="text-2xl font-bold mb-4">Signup</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label className="block mb-1">Full Name</label>
          <input
            name="fullName"
            className={`w-full p-2 border rounded ${errors.fullName ? 'border-red-500' : ''}`}
            {...register('fullName', { required: 'Full Name is required', minLength: { value: 2, message: 'Name must be at least 2 characters' } })}
          />
          {errors.fullName && <p className="text-red-500 text-sm">{errors.fullName.message}</p>}
        </div>

        <div>
          <label className="block mb-1">Email</label>
          <input
            name="email"
            type="email"
            className={`w-full p-2 border rounded ${errors.email ? 'border-red-500' : ''}`}
            {...register('email', { required: 'Email is required', pattern: { value: /^\S+@\S+$/i, message: 'Invalid email address' } })}
          />
          {errors.email && <p className="text-red-500 text-sm">{errors.email.message}</p>}
        </div>

        <div>
          <label className="block mb-1">Password</label>
          <input
            name="password"
            type="password"
            className={`w-full p-2 border rounded ${errors.password ? 'border-red-500' : ''}`}
            {...register('password', { 
              required: 'Password is required', 
              minLength: { value: 6, message: 'Password must be at least 6 characters' },
              pattern: { value: /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/, message: 'Password must contain at least one letter and one number' }
            })}
          />
          {errors.password && <p className="text-red-500 text-sm">{errors.password.message}</p>}
        </div>

        <div>
          <label className="block mb-1">Confirm Password</label>
          <input
            name="confirmPassword"
            type="password"
            className={`w-full p-2 border rounded ${errors.confirmPassword ? 'border-red-500' : ''}`}
            {...register('confirmPassword', { 
              required: 'Please confirm your password',
              validate: value => value === watch('password') || 'Passwords do not match'
            })}
          />
          {errors.confirmPassword && <p className="text-red-500 text-sm">{errors.confirmPassword.message}</p>}
        </div>

        <div>
          <label className="block mb-1">Phone Number</label>
          <input
            name="phone"
            type="tel"
            className={`w-full p-2 border rounded ${errors.phone ? 'border-red-500' : ''}`}
            placeholder="+92 300 1234567"
            {...register('phone', { 
              required: 'Phone number is required',
              pattern: { value: /^\+?\d{10,13}$/, message: 'Invalid phone number' }
            })}
          />
          {errors.phone && <p className="text-red-500 text-sm">{errors.phone.message}</p>}
        </div>

        <div>
          <label className="block mb-1">Gender</label>
          <select
            name="gender"
            className={`w-full p-2 border rounded ${errors.gender ? 'border-red-500' : ''}`}
            {...register('gender', { required: 'Please select a gender' })}
          >
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
          {errors.gender && <p className="text-red-500 text-sm">{errors.gender.message}</p>}
        </div>

        <div className="mb-4">
          <label className="inline-flex items-center">
            <input
              name="acceptedTerms"
              type="checkbox"
              className={`mr-2 ${errors.acceptedTerms ? 'border-red-500' : ''}`}
              {...register('acceptedTerms', { required: 'You must accept the terms and conditions' })}
            />
            I accept the <a href="/terms" className="text-blue-600 underline">Terms and Conditions</a>
          </label>
          {errors.acceptedTerms && <p className="text-red-500 text-sm">{errors.acceptedTerms.message}</p>}
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Signup
        </button>
      </form>
    </div>
  );
};

export default Signup;
