import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();

  const onSubmit = (data) => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.email === data.email && user.password === data.password) {
      localStorage.setItem('isLoggedIn', true);
      toast.success('Login successful! Welcome back!');
      navigate('/');
    } else {
      toast.error('Invalid email or password');
    }
  };

  return (
    <div className="flex justify-center mt-10 bg-white dark:bg-gray-900 min-h-screen">
      <form onSubmit={handleSubmit(onSubmit)} className="bg-white dark:bg-gray-800 p-6 rounded shadow-md w-80 border border-gray-300 dark:border-gray-600">
        <h2 className="text-xl font-bold mb-4 text-gray-900 dark:text-gray-200">Login</h2>
        <div className="mb-3">
          <input
            type="email"
            placeholder="Email"
            className={`w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 ${errors.email ? 'border-red-500 dark:border-red-500' : ''}`}
            {...register('email', { required: 'Email is required', pattern: { value: /^\S+@\S+$/i, message: 'Invalid email' } })}
          />
          {errors.email && <p className="text-red-500 dark:text-red-400 text-sm">{errors.email.message}</p>}
        </div>
        <div className="mb-3">
          <input
            type="password"
            placeholder="Password"
            className={`w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 ${errors.password ? 'border-red-500 dark:border-red-500' : ''}`}
            {...register('password', { required: 'Password is required', minLength: { value: 6, message: 'Password must be at least 6 characters' } })}
          />
          {errors.password && <p className="text-red-500 dark:text-red-400 text-sm">{errors.password.message}</p>}
        </div>
        <button type="submit" className="bg-blue-500 dark:bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
