import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import Button from '../components/Button';
import Input from '../components/Input';
import { Helmet } from 'react-helmet-async';

const Signup = () => {
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const { register: authRegister } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);

  const onSubmit = async (data) => {
    if (data.password !== data.confirmPassword) {
      toast.error("Password and Confirm Password don't match");
      return;
    }
    if (!data.acceptedTerms) {
      toast.error("Please accept the terms and conditions");
      return;
    }

    setIsLoading(true);
    try {
      const success = await authRegister({
        fullName: data.fullName,
        email: data.email,
        password: data.password,
        phone: data.phone,
        gender: data.gender
      });
      if (success) {
        toast.success("Signup successful! Please login to continue.");
        navigate('/login');
      }
    } catch (error) {
      toast.error('An error occurred during registration. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleSignup = () => {
    toast.info('Google SSO not implemented in demo. Contact admin for full implementation.');
  };

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8 bg-gradient-to-b from-gray-50 to-white dark:from-gray-900 dark:to-gray-800 text-gray-900 dark:text-gray-100 min-h-screen flex items-center justify-center">
      <Helmet>
        <title>Register - ShopEase</title>
        <meta name="description" content="Create a new ShopEase account." />
      </Helmet>
      <div className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md rounded-xl shadow-xl p-8 w-full max-w-md animate-fade-in">
        <h2 className="text-3xl font-bold mb-6 text-center">Register for ShopEase</h2>
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div>
            <Input
              name="fullName"
              type="text"
              placeholder="Enter your full name"
              {...register('fullName', {
                required: 'Full Name is required',
                minLength: { value: 2, message: 'Name must be at least 2 characters' }
              })}
              error={errors.fullName?.message}
              className="w-full"
            />
          </div>
          <div>
            <Input
              name="email"
              type="email"
              placeholder="Enter your email"
              {...register('email', {
                required: 'Email is required',
                pattern: { value: /^\S+@\S+\.\S+$/, message: 'Invalid email address' }
              })}
              error={errors.email?.message}
              className="w-full"
            />
          </div>
          <div>
            <Input
              name="password"
              type="password"
              placeholder="Enter your password"
              {...register('password', {
                required: 'Password is required',
                minLength: { value: 6, message: 'Password must be at least 6 characters' },
                pattern: { 
                  value: /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/, 
                  message: 'Password must contain at least one letter and one number' 
                }
              })}
              error={errors.password?.message}
              className="w-full"
            />
          </div>
          <div>
            <Input
              name="confirmPassword"
              type="password"
              placeholder="Confirm your password"
              {...register('confirmPassword', {
                required: 'Please confirm your password',
                validate: value => value === watch('password') || 'Passwords do not match'
              })}
              error={errors.confirmPassword?.message}
              className="w-full"
            />
          </div>
          <div>
            <Input
              name="phone"
              type="tel"
              placeholder="+123 456 7890"
              {...register('phone', {
                required: 'Phone number is required',
                pattern: { value: /^\+?\d{10,13}$/, message: 'Invalid phone number' }
              })}
              error={errors.phone?.message}
              className="w-full"
            />
          </div>
          <div>
            <select
              name="gender"
              className={`w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 ${errors.gender ? 'border-red-500 dark:border-red-500' : ''}`}
              {...register('gender', { required: 'Please select a gender' })}
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            {errors.gender && <p className="text-red-500 dark:text-red-400 text-sm">{errors.gender.message}</p>}
          </div>
          <div className="mb-4">
            <label className="inline-flex items-center text-gray-700 dark:text-gray-300">
              <input
                name="acceptedTerms"
                type="checkbox"
                className={`mr-2 border-gray-300 dark:border-gray-600 ${errors.acceptedTerms ? 'border-red-500 dark:border-red-500' : ''}`}
                {...register('acceptedTerms', { required: 'You must accept the terms and conditions' })}
              />
              I accept the <Link to="/terms" className="text-indigo-500 dark:text-indigo-400 hover:underline">Terms and Conditions</Link>
            </label>
            {errors.acceptedTerms && <p className="text-red-500 dark:text-red-400 text-sm">{errors.acceptedTerms.message}</p>}
          </div>
          <Button
            type="submit"
            variant="primary"
            size="lg"
            disabled={isLoading}
            className="w-full"
          >
            {isLoading ? 'Registering...' : 'Register'}
          </Button>
        </form>
        <div className="mt-6 text-center">
          <Button
            variant="danger"
            size="lg"
            onClick={handleGoogleSignup}
            className="w-full"
          >
            Sign Up with Google
          </Button>
        </div>
        <p className="mt-6 text-center text-gray-600 dark:text-gray-400">
          Already have an account?{' '}
          <Link to="/login" className="text-indigo-500 dark:text-indigo-400 hover:underline">
            Login
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Signup;
