import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { toast } from 'react-toastify';
import LoadingSpinner from '../components/LoadingSpinner';

const ProductDetails = () => {
  const { id } = useParams();
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [reviews, setReviews] = useState(
    JSON.parse(localStorage.getItem(`reviews-${id}`)) || []
  );

  // Fetch product details from FakeStoreAPI
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(`https://fakestoreapi.com/products/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch product');
        }
        const item = await response.json();
        const mappedProduct = {
          id: item.id,
          name: item.title,
          price: item.price,
          imageUrl: item.image,
          category: item.category.charAt(0).toUpperCase() + item.category.slice(1),
          rating: item.rating.rate,
          description: item.description,
        };
        setProduct(mappedProduct);
        setIsLoading(false);

        // Update recently viewed
        const recentlyViewed = JSON.parse(localStorage.getItem('recentlyViewed')) || [];
        const updatedViewed = [
          mappedProduct,
          ...recentlyViewed.filter(p => p.id !== mappedProduct.id),
        ].slice(0, 3);
        localStorage.setItem('recentlyViewed', JSON.stringify(updatedViewed));
      } catch (err) {
        setError(err.message);
        setIsLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  const handleRating = (value) => {
    setRating(value);
  };

  const handleReviewSubmit = (e) => {
    e.preventDefault();
    if (!rating || !review.trim()) {
      toast.error('Please provide a rating and review.');
      return;
    }
    const newReview = {
      id: Date.now(),
      rating,
      text: review,
      user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')).fullName : 'Anonymous',
      date: new Date().toLocaleDateString(),
    };
    const updatedReviews = [...reviews, newReview];
    setReviews(updatedReviews);
    localStorage.setItem(`reviews-${id}`, JSON.stringify(updatedReviews));
    toast.success('Review submitted successfully!');
    setRating(0);
    setReview('');
  };

  const handleQuantityChange = (change) => {
    setQuantity(prev => Math.max(1, prev + change));
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (error || !product) {
    return (
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 text-center text-red-500 dark:text-red-400 min-h-screen">
        <p>Error: {error || 'Product not found'}</p>
        <button
          onClick={() => window.location.reload()}
          className="mt-4 bg-blue-500 dark:bg-blue-600 text-white px-3 sm:px-4 py-1 sm:py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">{product.name}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 sm:gap-6">
        <img
          src={product.imageUrl}
          alt={product.name}
          className="w-full h-64 sm:h-80 md:h-96 object-contain rounded-lg"
        />
        <div>
          <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base mb-4">{product.description}</p>
          <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base mb-4">Category: {product.category}</p>
          <p className="text-xl sm:text-2xl font-bold mb-4">$ {product.price.toFixed(2)}</p>
          <p className="text-yellow-500 text-sm sm:text-base mb-4">
            {'★'.repeat(Math.floor(product.rating))}{'☆'.repeat(5 - Math.floor(product.rating))}
          </p>
          <div className="flex items-center gap-4 mb-4">
            <label className="text-sm sm:text-lg text-gray-700 dark:text-gray-300">Quantity:</label>
            <div className="flex items-center gap-2">
              <button
                onClick={() => handleQuantityChange(-1)}
                className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-2 sm:px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
                disabled={quantity <= 1}
              >
                -
              </button>
              <span className="px-3 sm:px-4 py-1 border rounded bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-gray-200 text-sm sm:text-base">{quantity}</span>
              <button
                onClick={() => handleQuantityChange(1)}
                className="bg-gray-300 dark:bg-gray-600 text-gray-900 dark:text-gray-200 px-2 sm:px-3 py-1 rounded hover:bg-gray-400 dark:hover:bg-gray-700"
              >
                +
              </button>
            </div>
          </div>
          <div className="flex flex-col sm:flex-row gap-2 sm:gap-4">
            <button
              onClick={() => addToCart(product, quantity)}
              className="bg-blue-500 dark:bg-blue-600 text-white px-4 sm:px-6 py-1 sm:py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
            >
              Add to Cart
            </button>
            <button
              onClick={() => addToWishlist(product)}
              className="bg-gray-500 dark:bg-gray-600 text-white px-4 sm:px-6 py-1 sm:py-2 rounded hover:bg-gray-600 dark:hover:bg-gray-700 text-sm sm:text-base"
            >
              Add to Wishlist
            </button>
          </div>
        </div>
      </div>
      <div className="mt-6 sm:mt-8">
        <h3 className="text-xl sm:text-2xl font-semibold mb-4">Customer Reviews</h3>
        {reviews.length === 0 ? (
          <p className="text-gray-500 dark:text-gray-400 text-sm sm:text-base">No reviews yet. Be the first to review!</p>
        ) : (
          <div className="space-y-4">
            {reviews.map(review => (
              <div key={review.id} className="border-b pb-4 border-gray-300 dark:border-gray-600">
                <div className="flex items-center gap-2">
                  <span className="text-yellow-500 text-sm sm:text-base">
                    {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
                  </span>
                  <span className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">by {review.user} on {review.date}</span>
                </div>
                <p className="text-gray-700 dark:text-gray-300 text-sm sm:text-base">{review.text}</p>
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="mt-6 sm:mt-8">
        <h3 className="text-xl sm:text-2xl font-semibold mb-4">Write a Review</h3>
        <form onSubmit={handleReviewSubmit} className="space-y-4 max-w-full sm:max-w-md">
          <div>
            <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">Rating</label>
            <div className="flex gap-1">
              {[1, 2, 3, 4, 5].map(star => (
                <span
                  key={star}
                  className={`cursor-pointer text-lg sm:text-2xl ${star <= rating ? 'text-yellow-500' : 'text-gray-300 dark:text-gray-500'}`}
                  onClick={() => handleRating(star)}
                >
                  ★
                </span>
              ))}
            </div>
          </div>
          <div>
            <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">Your Review</label>
            <textarea
              value={review}
              onChange={(e) => setReview(e.target.value)}
              className="w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base"
              rows="4"
              placeholder="Write your review here..."
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 dark:bg-blue-600 text-white px-3 sm:px-4 py-1 sm:py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
          >
            Submit Review
          </button>
        </form>
      </div>
    </div>
  );
};

export default ProductDetails;
