import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiSearch, FiShoppingCart, FiHeart, FiUser, FiMenu, FiX } from 'react-icons/fi';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { useAuth } from '../context/AuthContext';
import '../styles.css';

const Navbar = ({ onSearch }) => {
  const { cart = [] } = useCart();
  const { wishlist = [] } = useWishlist();
  const { user, isLoggedIn, logout } = useAuth();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);

  const handleSearch = (e) => {
    e.preventDefault();
    if (onSearch) onSearch(searchTerm);
    navigate('/');
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleLogout = () => {
    logout();
    setIsProfileOpen(false);
    setIsMobileMenuOpen(false);
  };

  return (
    <nav className="sticky top-0 z-50 bg-white/80 dark:bg-gray-800/80 backdrop-blur-md shadow-md">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
        <Link to="/" className="text-2xl font-bold text-indigo-600 dark:text-indigo-400">ShopEase</Link>
        <div className="hidden md:flex flex-1 mx-8">
          <form onSubmit={handleSearch} className="w-full max-w-md">
            <div className="relative">
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full p-3 pl-10 border rounded-xl bg-white/80 dark:bg-gray-700/50 border-gray-200 dark:border-gray-600 text-gray-900 dark:text-white focus:ring-2 focus:ring-indigo-500 transition-all duration-300 shadow-sm text-sm"
              />
              <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 dark:text-gray-400" />
            </div>
          </form>
        </div>
        <div className="flex items-center gap-4">
          <Link to="/cart" className="relative">
            <FiShoppingCart className="text-xl text-gray-600 dark:text-gray-300 hover:text-indigo-500 dark:hover:text-indigo-400 transition-colors duration-200" />
            {cart.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {cart.reduce((sum, item) => sum + (item.quantity || 0), 0)}
              </span>
            )}
          </Link>
          <Link to="/wishlist" className="relative">
            <FiHeart className="text-xl text-gray-600 dark:text-gray-300 hover:text-indigo-500 dark:hover:text-indigo-400 transition-colors duration-200" />
            {wishlist.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                {wishlist.length}
              </span>
            )}
          </Link>
          <div className="relative">
            <button
              onClick={() => setIsProfileOpen(!isProfileOpen)}
              className="text-xl text-gray-600 dark:text-gray-300 hover:text-indigo-500 dark:hover:text-indigo-400 transition-colors duration-200"
            >
              <FiUser />
            </button>
            {isProfileOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-xl shadow-xl py-2 animate-slide-down">
                {isLoggedIn ? (
                  <>
                    <Link
                      to="/profile"
                      className="block px-4 py-2 text-gray-700 dark:text-gray-200 hover:bg-indigo-100 dark:hover:bg-indigo-900"
                      onClick={() => setIsProfileOpen(false)}
                    >
                      Profile
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 text-gray-700 dark:text-gray-200 hover:bg-indigo-100 dark:hover:bg-indigo-900"
                    >
                      Logout
                    </button>
                  </>
                ) : (
                  <Link
                    to="/login"
                    className="block px-4 py-2 text-gray-700 dark:text-gray-200 hover:bg-indigo-100 dark:hover:bg-indigo-900"
                    onClick={() => setIsProfileOpen(false)}
                  >
                    Login
                  </Link>
                )}
              </div>
            )}
          </div>
          <button className="md:hidden text-xl text-gray-600 dark:text-gray-300" onClick={toggleMobileMenu}>
            {isMobileMenuOpen ? <FiX /> : <FiMenu />}
          </button>
        </div>
      </div>
      <div
        className={`md:hidden bg-white/80 dark:bg-gray-600/80 backdrop-blur-md px-4 py-2 transition-transform duration-300 ease-in-out ${
          isMobileMenuOpen ? 'translate-y-0' : '-translate-y-full'
        }`}
      >
        <form onSubmit={handleSearch} className="mb-4">
          <div className="relative">
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-3 pl-10 border rounded-xl bg-white/80 dark:bg-gray-700/50 border-gray-200 dark:border-gray-600 text-gray-900 dark:text-white focus:ring-2 focus:ring-indigo-500 transition-all duration-300 shadow-sm text-sm"
            />
            <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 dark:text-gray-400" />
          </div>
        </form>
        <Link to="/" className="block py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={toggleMobileMenu}>Home</Link>
        <Link to="/cart" className="block py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={toggleMobileMenu}>Cart</Link>
        <Link to="/wishlist" className="block py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={toggleMobileMenu}>Wishlist</Link>
        {isLoggedIn ? (
          <>
            <Link to="/profile" className="block py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={toggleMobileMenu}>Profile</Link>
            <button className="block w-full text-left py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <Link to="/login" className="block py-2 text-gray-600 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400" onClick={toggleMobileMenu}>Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
