import React from 'react';

const SearchSuggestions = ({ searchTerm, products, setSearchTerm }) => {
  const filteredSuggestions = products
    .filter(product =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .slice(0, 5);

  return (
    <div className="absolute z-10 w-full bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 rounded-b shadow-lg mt-1">
      {filteredSuggestions.length > 0 ? (
        filteredSuggestions.map(product => (
          <div
            key={product.id}
            className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 cursor-pointer text-gray-900 dark:text-gray-200"
            onClick={() => setSearchTerm(product.name)}
          >
            {product.name}
          </div>
        ))
      ) : (
        <div className="px-4 py-2 text-gray-500 dark:text-gray-400">
          No matching products found
        </div>
      )}
    </div>
  );
};

export default SearchSuggestions;
