import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import LoadingSpinner from '../components/LoadingSpinner';

const products = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/300' },
  { id: 2, name: 'Smartphone', price: 45000, imageUrl: 'https://via.placeholder.com/300' },
];

const Home = () => {
  const { addToCart } = useCart();
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredProducts, setFilteredProducts] = useState(products);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    setFilteredProducts(
      products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm]);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">All Products</h2>
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search products..."
          className="w-full md:w-1/3 p-2 border rounded"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {filteredProducts.length === 0 ? (
          <p>No products found.</p>
        ) : (
          filteredProducts.map(product => (
            <div key={product.id} className="border p-4 rounded shadow">
              <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
              <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
              <p className="text-gray-700 mb-2">Rs. {product.price}</p>
              <div className="flex justify-between">
                <Link to={`/product/${product.id}`} className="text-blue-500 hover:underline">View Details</Link>
                <button
                  onClick={() => addToCart(product)}
                  className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Home;
