import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';

const pageSize = 9;               // ek baar mein kitne products lao

const Category = () => {
  const { name } = useParams();   // URL se category name
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const navigate = useNavigate();

  const [products, setProducts] = useState([]);          // sab fetched products
  const [page, setPage] = useState(1);                   // current page #
  const [hasMore, setHasMore] = useState(true);          // aur data bacha hai?
  const [isLoading, setIsLoading] = useState(false);     // spinner control
  const [compareProducts, setCompareProducts] = useState(
    JSON.parse(localStorage.getItem('compareProducts')) || []
  );

  const loadMoreRef      = useRef(null);                 // invisible div for observer
  const observerRef      = useRef(null);

  /* ---------- 1. Reset when category changes ---------- */
  useEffect(() => {
    setProducts([]);
    setPage(1);
    setHasMore(true);
  }, [name]);

  /* ---------- 2. Fetch data whenever page OR category changes ---------- */
  useEffect(() => {
    if (!hasMore) return;            // agar sab fetch ho chuka
    setIsLoading(true);

    const skip = (page - 1) * pageSize;
    fetch(
      `https://dummyjson.com/products/category/${name.toLowerCase()}?limit=${pageSize}&skip=${skip}`
    )
      .then((res) => res.json())
      .then((data) => {
        /* DummyJSON returns { products: [...], total, skip, limit } */
        setProducts((prev) => [...prev, ...data.products]);
        setHasMore(skip + data.limit < data.total); // aur aage data?
      })
      .catch((err) => console.error('Fetch error:', err))
      .finally(() => setIsLoading(false));
  }, [name, page]);

  /* ---------- 3. Intersection Observer for infinite scroll ---------- */
  useEffect(() => {
    if (!loadMoreRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore && !isLoading) {
          setPage((prev) => prev + 1);
        }
      },
      { threshold: 0.1 }
    );

    observer.observe(loadMoreRef.current);
    observerRef.current = observer;

    return () => observer.disconnect();
  }, [hasMore, isLoading]);

  /* ---------- 4. Compare list helpers ---------- */
  const handleCompare = (product) => {
    const exists = compareProducts.some((p) => p.id === product.id);
    const updated = exists
      ? compareProducts.filter((p) => p.id !== product.id)
      : compareProducts.length < 4
      ? [...compareProducts, product]
      : compareProducts;

    setCompareProducts(updated);
    localStorage.setItem('compareProducts', JSON.stringify(updated));
  };

  const startComparison = () => {
    if (compareProducts.length >= 2) {
      navigate('/compare');
    }
  };

  /* ---------- 5. UI ---------- */
  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6 capitalize">{name} Products</h2>

      {/* Compare button */}
      <div className="mb-6">
        <button
          onClick={startComparison}
          disabled={compareProducts.length < 2}
          className={`px-4 py-2 rounded text-white ${
            compareProducts.length < 2
              ? 'bg-gray-400'
              : 'bg-blue-500 hover:bg-blue-600 dark:bg-blue-600 dark:hover:bg-blue-700'
          }`}
        >
          Compare Selected ({compareProducts.length}/4)
        </button>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.length === 0 && !isLoading ? (
          <p className="text-xl text-gray-500 dark:text-gray-400">
            No products found in this category.
          </p>
        ) : (
          products.map((product) => (
            <div
              key={product.id}
              className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200 relative"
            >
              {/* compare checkbox */}
              <input
                type="checkbox"
                checked={compareProducts.some((p) => p.id === product.id)}
                onChange={() => handleCompare(product)}
                disabled={
                  !compareProducts.some((p) => p.id === product.id) &&
                  compareProducts.length >= 4
                }
                className="absolute top-2 right-2"
              />

              {/* product image */}
              <img
                src={product.thumbnail || product.images?.[0]}
                alt={product.title}
                className="w-full h-48 object-cover rounded"
              />

              {/* details */}
              <h3 className="text-xl font-semibold mt-2">{product.title}</h3>
              <p className="text-gray-600 dark:text-gray-400 capitalize">
                {product.category}
              </p>
              <p className="text-gray-700 dark:text-gray-300 mb-2">
                Rs. {product.price}
              </p>
              <p className="text-yellow-500 mb-2">
                {'★'.repeat(Math.floor(product.rating))}
                {'☆'.repeat(5 - Math.floor(product.rating))}
              </p>

              {/* actions */}
              <div className="flex justify-between gap-2">
                <Link
                  to={`/product/${product.id}`}
                  className="text-blue-500 dark:text-blue-400 hover:underline"
                >
                  View Details
                </Link>
                <div className="flex gap-2">
                  <button
                    onClick={() => addToCart(product)}
                    className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => addToWishlist(product)}
                    className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
                  >
                    Add to Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Infinite-scroll trigger */}
      {hasMore && (
        <div ref={loadMoreRef} className="flex justify-center py-4">
          <LoadingSpinner />
        </div>
      )}
    </div>
  );
};

export default Category;
