import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const { cartItems } = useCart();
  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem('isLoggedIn');
  const [isOpen, setIsOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    navigate('/login');
  };

  return (
    <nav className="bg-gray-800 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-2xl font-bold">MyShop</Link>
        <button
          className="md:hidden text-white focus:outline-none"
          onClick={() => setIsOpen(!isOpen)}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={isOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16m-7 6h7'} />
          </svg>
        </button>
        <div className={`md:flex space-x-4 ${isOpen ? 'block' : 'hidden'} md:block`}>
          <Link to="/" className="text-white hover:text-gray-300 block md:inline">Home</Link>
          <Link to="/cart" className="text-white hover:text-gray-300 block md:inline">
            Cart ({totalItems})
          </Link>
          <Link to="/checkout" className="text-white hover:text-gray-300 block md:inline">Checkout</Link>
          {!isLoggedIn ? (
            <>
              <Link to="/login" className="text-white hover:text-gray-300 block md:inline">Login</Link>
              <Link to="/signup" className="text-white hover:text-gray-300 block md:inline">Signup</Link>
            </>
          ) : (
            <button
              onClick={handleLogout}
              className="text-white hover:text-gray-300 bg-red-600 px-3 py-1 rounded block md:inline"
            >
              Logout
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
