import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { useTheme } from '../context/ThemeContext';

const Navbar = () => {
  const { cartItems } = useCart();
  const { wishlistItems } = useWishlist();
  const { theme, toggleTheme } = useTheme();
  const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const totalWishlistItems = wishlistItems.length;
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem('isLoggedIn');
  const [isOpen, setIsOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    navigate('/login');
  };

  return (
    <nav className="bg-gray-800 dark:bg-gray-950 p-4 sm:p-6">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        <Link to="/" className="text-white dark:text-gray-200 text-xl sm:text-2xl font-bold">MyShop</Link>
        <button
          className="md:hidden text-white dark:text-gray-200 focus:outline-none"
          onClick={() => setIsOpen(!isOpen)}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={isOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16m-7 6h7'} />
          </svg>
        </button>
        <div className={`w-full md:w-auto md:flex md:space-x-4 ${isOpen ? 'block mt-4' : 'hidden'} md:block`}>
          <Link to="/" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Home</Link>
          <Link to="/cart" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">
            Cart ({totalItems})
          </Link>
          <Link to="/wishlist" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">
            Wishlist ({totalWishlistItems})
          </Link>
          <Link to="/checkout" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Checkout</Link>
          {isLoggedIn ? (
            <>
              <Link to="/profile" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Profile</Link>
              <Link to="/orders" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Orders</Link>
              <button
                onClick={handleLogout}
                className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 bg-red-600 dark:bg-red-700 px-3 sm:px-4 py-1 sm:py-2 rounded block md:inline text-sm sm:text-base"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Login</Link>
              <Link to="/signup" className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 block py-2 md:inline md:py-0 text-sm sm:text-base">Signup</Link>
            </>
          )}
          <button
            onClick={toggleTheme}
            className="text-white dark:text-gray-200 hover:text-gray-300 dark:hover:text-gray-400 px-3 sm:px-4 py-1 sm:py-2 rounded block md:inline text-sm sm:text-base"
          >
            {theme === 'light' ? 'üåô Dark Mode' : '‚òÄÔ∏è Light Mode'}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
