import React from 'react';
import { Link } from 'react-router-dom';
import Button from '../components/Button';
import ProductList from '../components/ProductList';

const Home = () => {
  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      {/* Hero Section */}
      <section className="mb-8 text-center">
        <h1 className="text-3xl sm:text-4xl font-bold mb-4">Welcome to ShopEase</h1>
        <p className="text-gray-600 dark:text-gray-400 mb-6 text-lg">
          Discover the best products at unbeatable prices!
        </p>
        <Link to="/category/all">
          <Button variant="primary" size="lg">
            Shop Now
          </Button>
        </Link>
      </section>

      {/* Explore Our Products Section */}
      <section className="mb-8">
        <h2 className="text-2xl sm:text-3xl font-bold mb-4 text-center">Explore Our Products</h2>
        <div className="bg-gray-100 dark:bg-gray-800 rounded-xl p-4">
          <ProductList />
        </div>
      </section>
    </div>
  );
};

export default Home;
