import React from 'react';
import { useParams } from 'react-router-dom';

const ProductDetails = () => {
  const { id } = useParams();
  const dummyProduct = {
    id: id,
    name: `Product ${id}`,
    description: 'This is a sample product description.',
    price: 50000,
    imageUrl: 'https://via.placeholder.com/300',
  };

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">{dummyProduct.name}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <img src={dummyProduct.imageUrl} alt={dummyProduct.name} className="w-full h-96 object-cover rounded-lg" />
        <div>
          <p className="text-gray-600 mb-4">{dummyProduct.description}</p>
          <p className="text-2xl font-bold mb-4">Rs. {dummyProduct.price}</p>
          <button className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600">Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
