import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useOrder } from '../context/OrderContext';
import { toast } from 'react-toastify';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const { addOrder } = useOrder();
  const navigate = useNavigate();
  const { register, handleSubmit, formState: { errors } } = useForm();
  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  if (cartItems.length === 0) {
    return (
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 text-center bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
        <h2 className="text-xl sm:text-2xl font-bold mb-4">Your Cart is Empty</h2>
        <p className="text-base sm:text-lg text-gray-600 dark:text-gray-400">Add some items to your cart before checking out.</p>
        <button
          onClick={() => navigate('/')}
          className="mt-4 bg-blue-500 dark:bg-blue-600 text-white px-3 sm:px-4 py-1 sm:py-2 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
        >
          Continue Shopping
        </button>
      </div>
    );
  }

  const onSubmit = (data) => {
    addOrder(cartItems, totalPrice);
    clearCart();
    toast.success('Order placed successfully!');
    navigate('/order-confirmation');
  };

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">Checkout</h2>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6">
        <div>
          <h3 className="text-xl sm:text-2xl font-semibold mb-4">Order Summary</h3>
          <div className="space-y-4">
            {cartItems.map(item => (
              <div
                key={item.id}
                className="flex flex-col sm:flex-row items-start sm:items-center justify-between border-b pb-2 border-gray-300 dark:border-gray-600"
              >
                <div className="flex items-center space-x-4 w-full sm:w-auto">
                  <img
                    src={item.imageUrl}
                    alt={item.name}
                    className="w-16 h-16 sm:w-20 sm:h-20 object-contain rounded"
                  />
                  <div>
                    <h4 className="text-base sm:text-lg font-semibold truncate">{item.name}</h4>
                    <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">Quantity: {item.quantity}</p>
                    <p className="text-gray-600 dark:text-gray-400 text-sm sm:text-base">$ {item.price.toFixed(2)}</p>
                  </div>
                </div>
                <p className="text-gray-700 dark:text-gray-300 text-sm sm:text-base mt-2 sm:mt-0">$ {(item.price * item.quantity).toFixed(2)}</p>
              </div>
            ))}
            <div className="text-base sm:text-xl font-bold mt-4">
              Total: $ {totalPrice.toFixed(2)}
            </div>
          </div>
        </div>
        <div>
          <h3 className="text-xl sm:text-2xl font-semibold mb-4">Shipping Information</h3>
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div>
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">Full Name</label>
              <input
                className={`w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base ${errors.fullName ? 'border-red-500 dark:border-red-500' : ''}`}
                {...register('fullName', { required: 'Full Name is required' })}
              />
              {errors.fullName && <p className="text-red-500 dark:text-red-400 text-sm">{errors.fullName.message}</p>}
            </div>
            <div>
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">Address</label>
              <input
                className={`w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base ${errors.address ? 'border-red-500 dark:border-red-500' : ''}`}
                {...register('address', { required: 'Address is required' })}
              />
              {errors.address && <p className="text-red-500 dark:text-red-400 text-sm">{errors.address.message}</p>}
            </div>
            <div>
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">City</label>
              <input
                className={`w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base ${errors.city ? 'border-red-500 dark:border-red-500' : ''}`}
                {...register('city', { required: 'City is required' })}
              />
              {errors.city && <p className="text-red-500 dark:text-red-400 text-sm">{errors.city.message}</p>}
            </div>
            <div>
              <label className="block mb-1 text-gray-700 dark:text-gray-300 text-sm sm:text-base">Postal Code</label>
              <input
                className={`w-full p-2 sm:p-3 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600 text-sm sm:text-base ${errors.postalCode ? 'border-red-500 dark:border-red-500' : ''}`}
                {...register('postalCode', { required: 'Postal Code is required' })}
              />
              {errors.postalCode && <p className="text-red-500 dark:text-red-400 text-sm">{errors.postalCode.message}</p>}
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 dark:bg-blue-600 text-white py-2 sm:py-3 rounded hover:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base"
            >
              Place Order
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
