import React from 'react';
import { Link } from 'react-router-dom';
import { useWishlist } from '../context/WishlistContext';
import { toast } from 'react-toastify';

const Wishlist = () => {
  const { wishlistItems, removeFromWishlist, clearWishlist } = useWishlist();

  if (wishlistItems.length === 0) {
    return <div className="p-8 text-xl text-center">Your wishlist is empty.</div>;
  }

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-3xl font-bold mb-6">Your Wishlist</h2>
      <button
        onClick={clearWishlist}
        className="mb-6 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
      >
        Clear Wishlist
      </button>
      <div className="space-y-6">
        {wishlistItems.map(item => (
          <div key={item.id} className="flex items-center justify-between border-b pb-4">
            <div className="flex items-center space-x-4">
              <img src={item.imageUrl} alt={item.name} className="w-24 h-24 object-cover rounded" />
              <div>
                <h3 className="text-xl font-semibold">{item.name}</h3>
                <p className="text-gray-600">{item.category}</p>
                <p className="text-gray-600">Rs. {item.price}</p>
              </div>
            </div>
            <div>
              <Link to={`/product/${item.id}`} className="text-blue-500 hover:underline mr-4">View Details</Link>
              <button
                onClick={() => removeFromWishlist(item.id)}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Remove
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wishlist;
