import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import LoadingSpinner from '../components/LoadingSpinner';
import SearchSuggestions from '../components/SearchSuggestions';

const products = [
  { id: 1, name: 'Laptop', price: 85000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics' },
  { id: 2, name: 'Smartphone', price: 45000, imageUrl: 'https://via.placeholder.com/300', category: 'Electronics' },
  { id: 3, name: 'T-Shirt', price: 1500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing' },
  { id: 4, name: 'Jeans', price: 2500, imageUrl: 'https://via.placeholder.com/300', category: 'Clothing' },
];

const Home = () => {
  const { addToCart } = useCart();
  const { addToWishlist } = useWishlist();
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [sortBy, setSortBy] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 100000]);
  const [filteredProducts, setFilteredProducts] = useState(products);
  const categories = ['All', 'Electronics', 'Clothing'];
  const sortOptions = [
    { value: 'default', label: 'Default' },
    { value: 'price-asc', label: 'Price: Low to High' },
    { value: 'price-desc', label: 'Price: High to Low' },
    { value: 'name-asc', label: 'Name: A to Z' },
    { value: 'name-desc', label: 'Name: Z to A' },
  ];

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    let result = products;
    if (category !== 'All') {
      result = result.filter(product => product.category === category);
    }
    if (searchTerm) {
      result = result.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    result = result.filter(product => 
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );
    if (sortBy !== 'default') {
      result = [...result].sort((a, b) => {
        if (sortBy === 'price-asc') return a.price - b.price;
        if (sortBy === 'price-desc') return b.price - a.price;
        if (sortBy === 'name-asc') return a.name.localeCompare(b.name);
        if (sortBy === 'name-desc') return b.name.localeCompare(a.name);
        return 0;
      });
    }
    setFilteredProducts(result);
  }, [searchTerm, category, sortBy, priceRange]);

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto py-8 bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-200 min-h-screen">
      <h2 className="text-3xl font-bold mb-6">All Products</h2>
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <div className="relative w-full md:w-1/3">
          <input
            type="text"
            placeholder="Search products..."
            className="w-full p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {searchTerm && (
            <SearchSuggestions
              searchTerm={searchTerm}
              products={products}
              setSearchTerm={setSearchTerm}
            />
          )}
        </div>
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full md:w-1/4 p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
        >
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="w-full md:w-1/4 p-2 border rounded bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200 border-gray-300 dark:border-gray-600"
        >
          {sortOptions.map(option => (
            <option key={option.value} value={option.value}>{option.label}</option>
          ))}
        </select>
      </div>
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Price Range</h3>
        <div className="flex items-center gap-4">
          <input
            type="range"
            min="0"
            max="100000"
            value={priceRange[0]}
            onChange={(e) => setPriceRange([+e.target.value, priceRange[1]])}
            className="w-1/2"
          />
          <input
            type="range"
            min="0"
            max="100000"
            value={priceRange[1]}
            onChange={(e) => setPriceRange([priceRange[0], +e.target.value])}
            className="w-1/2"
          />
        </div>
        <p className="text-gray-600 dark:text-gray-400 mt-2">
          Price: Rs. {priceRange[0]} - Rs. {priceRange[1]}
        </p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.length === 0 ? (
          <p className="text-xl text-gray-500 dark:text-gray-400">No products found.</p>
        ) : (
          filteredProducts.map(product => (
            <div
              key={product.id}
              className="border p-4 rounded shadow bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 transform hover:scale-105 hover:shadow-xl transition-all duration-200"
            >
              <img src={product.imageUrl} alt={product.name} className="w-full h-48 object-cover rounded" />
              <h3 className="text-xl font-semibold mt-2">{product.name}</h3>
              <p className="text-gray-600 dark:text-gray-400">{product.category}</p>
              <p className="text-gray-700 dark:text-gray-300 mb-2">Rs. {product.price}</p>
              <div className="flex justify-between gap-2">
                <Link to={`/product/${product.id}`} className="text-blue-500 dark:text-blue-400 hover:underline">
                  View Details
                </Link>
                <div className="flex gap-2">
                  <button
                    onClick={() => addToCart(product)}
                    className="bg-blue-500 dark:bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-600 dark:hover:bg-blue-700"
                  >
                    Add to Cart
                  </button>
                  <button
                    onClick={() => addToWishlist(product)}
                    className="bg-gray-500 dark:bg-gray-600 text-white px-4 py-1 rounded hover:bg-gray-600 dark:hover:bg-gray-700"
                  >
                    Add to Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Home;
